/* global log, Class, Detect, Modules */

define(['jquery'], function($) {
    return Class.extend({
        init: function() {
            var self = this;

            self.config = null;

            self.body = $('body');
            self.parchment = $('#parchment');
            self.container = $('#container');
            self.window = $(window);
            self.canvas = $('#canvas');
            self.border = $('#border');

            self.intro = $('#intro');

            self.loginButton = $('#login');
            self.createButton = $('#play');
            self.registerButton = $('#newCharacter');
            self.helpButton = $('#helpButton');
            self.cancelButton = $('#cancelButton');
            self.yes = $('#yes');
            self.no = $('#no');
            self.loading = $('.loader');

            self.respawn = $('#respawn');

            self.rememberMe = $('#rememberMe');
            self.guest = $('#guest');

            self.about = $('#toggle-about');
            self.credits = $('#toggle-credits');
            self.discord = $('#toggle-discord');
            self.git = $('#toggle-git');

            self.footer = $('footer');

            self.loginFields = [];
            self.registerFields = [];

            self.game = null;
            self.parchmentAnimating = false;
            self.loggingIn = false;

            self.sendStatus('Initializing the main app');

            self.updateOrientation();
            self.load();
        },

        load: function() {
            var self = this;

            self.loginButton.click(function() {
                self.login();
            });

            self.createButton.click(function() {
                self.login();
            });

            self.registerButton.click(function() {
                self.openScroll('loadCharacter', 'createCharacter');
            });

            self.cancelButton.click(function() {
                self.openScroll('createCharacter', 'loadCharacter');
            });

            self.parchment.click(function() {
                if (
                    self.parchment.hasClass('about') ||
                    self.parchment.hasClass('credits') ||
                    self.parchment.hasClass('git')
                ) {
                    self.parchment.removeClass('about credits git');
                    self.displayScroll('loadCharacter');
                }
            });

            self.about.click(function() {
                self.displayScroll('about');
            });

            self.credits.click(function() {
                self.displayScroll('credits');
            });

            self.discord.click(function() {
                window.open('https://discord.gg/MmbGAaw');
            });

            self.git.click(function() {
                self.displayScroll('git');
            });

            self.rememberMe.click(function() {
                if (!self.game || !self.game.storage) return;

                var active = self.rememberMe.hasClass('active');

                self.rememberMe.toggleClass('active');

                self.game.storage.toggleRemember(!active);
            });

            self.guest.click(function() {
                if (!self.game) return;

                self.guest.toggleClass('active');
            });

            self.respawn.click(function() {
                if (!self.game || !self.game.player || !self.game.player.dead)
                    return;

                self.game.respawn();
            });

            window.scrollTo(0, 1);

            self.window.resize(function() {
                if (self.game) self.game.resize();
            });

            $.getJSON('data/config.json', function(json) {
                self.config = json;

                if (self.readyCallback) self.readyCallback();
            });

            $(document).bind('keydown', function(e) {
                if (e.which === Modules.Keys.Enter) return false;
            });

            $(document).keydown(function(e) {
                var key = e.which;

                if (!self.game) return;

                self.body.focus();

                if (key === Modules.Keys.Enter && !self.game.started) {
                    self.login();
                    return;
                }

                if (self.game.started)
                    self.game.handleInput(Modules.InputType.Key, key);
            });

            $(document).keyup(function(e) {
                var key = e.which;

                if (!self.game || !self.game.started) return;

                self.game.input.keyUp(key);
            });

            $(document).mousemove(function(event) {
                if (
                    !self.game ||
                    !self.game.input ||
                    !self.game.started ||
                    event.target.id !== 'textCanvas'
                )
                    return;

                self.game.input.setCoords(event);
                self.game.input.moveCursor();
            });

            self.canvas.click(function(event) {
                if (!self.game || !self.game.started || event.button !== 0)
                    return;

                window.scrollTo(0, 1);

                self.game.input.handle(Modules.InputType.LeftClick, event);
            });

            $('input[type="range"]').on('input', function() {
                self.updateRange($(this));
            });
        },

        login: function() {
            var self = this;

            if (
                self.loggingIn ||
                !self.game ||
                !self.game.loaded ||
                self.statusMessage ||
                !self.verifyForm()
            )
                return;

            self.toggleLogin(true);
            self.game.connect();
        },

        fadeMenu: function() {
            var self = this;

            self.updateLoader(null);

            setTimeout(function() {
                self.body.addClass('game');
                self.body.addClass('started');

                self.body.removeClass('intro');

                self.footer.hide();
            }, 500);
        },

        showMenu: function() {
            var self = this;

            self.body.removeClass('game');
            self.body.removeClass('started');
            self.body.addClass('intro');

            self.footer.show();
        },

        showDeath: function() {},

        openScroll: function(origin, destination) {
            var self = this;

            if (!destination || self.loggingIn) return;

            self.cleanErrors();

            if (!Detect.isMobile()) {
                if (self.parchmentAnimating) return;

                self.parchmentAnimating = true;

                self.parchment.toggleClass('animate').removeClass(origin);

                setTimeout(
                    function() {
                        self.parchment
                            .toggleClass('animate')
                            .addClass(destination);
                        self.parchmentAnimating = false;
                    },
                    Detect.isTablet() ? 0 : 1000
                );
            } else self.parchment.removeClass(origin).addClass(destination);
        },

        displayScroll: function(content) {
            var self = this,
                state = self.parchment.attr('class');

            if (self.game.started) {
                self.parchment.removeClass().addClass(content);

                self.body
                    .removeClass('credits legal about')
                    .toggleClass(content);

                if (self.game.player) self.body.toggleClass('death');

                if (content !== 'about') self.helpButton.removeClass('active');
            } else if (state !== 'animate') {
                self.openScroll(
                    state,
                    state === content ? 'loadCharacter' : content
                );
            }
        },

        verifyForm: function() {
            var self = this,
                activeForm = self.getActiveForm();

            if (activeForm === 'null') return;

            switch (activeForm) {
                case 'loadCharacter':
                    var nameInput = $('#loginNameInput'),
                        passwordInput = $('#loginPasswordInput');

                    if (self.loginFields.length === 0)
                        self.loginFields = [nameInput, passwordInput];

                    if (!nameInput.val() && !self.isGuest()) {
                        self.sendError(nameInput, 'Please enter a username.');
                        return false;
                    }

                    if (!passwordInput.val() && !self.isGuest()) {
                        self.sendError(
                            passwordInput,
                            'Please enter a password.'
                        );
                        return false;
                    }

                    break;

                case 'createCharacter':
                    var characterName = $('#registerNameInput'),
                        registerPassword = $('#registerPasswordInput'),
                        registerPasswordConfirmation = $(
                            '#registerPasswordConfirmationInput'
                        ),
                        email = $('#registerEmailInput');

                    if (self.registerFields.length === 0) {
                        self.registerFields = [
                            characterName,
                            registerPassword,
                            registerPasswordConfirmation,
                            email
                        ];
                    }

                    if (!characterName.val()) {
                        self.sendError(
                            characterName,
                            'A username is necessary you silly.'
                        );
                        return false;
                    }

                    if (!registerPassword.val()) {
                        self.sendError(
                            registerPassword,
                            'You must enter a password.'
                        );
                        return false;
                    }

                    if (
                        registerPasswordConfirmation.val() !==
                        registerPassword.val()
                    ) {
                        self.sendError(
                            registerPasswordConfirmation,
                            'The passwords do not match!'
                        );
                        return false;
                    }

                    if (!email.val() || !self.verifyEmail(email.val())) {
                        self.sendError(email, 'An email is required!');
                        return false;
                    }

                    break;
            }

            return true;
        },

        verifyEmail: function(email) {
            return /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(
                email
            );
        },

        sendStatus: function(message) {
            var self = this;

            self.cleanErrors();

            self.statusMessage = message;

            if (!message) return;

            $('<span></span>', {
                class: 'status blink',
                text: message
            }).appendTo('.validation-summary');

            $('.status').append(
                '<span class="loader__dot">.</span><span class="loader__dot">.</span><span class="loader__dot">.</span>'
            );
        },

        sendError: function(field, error) {
            this.cleanErrors();

            $('<span></span>', {
                class: 'validation-error blink',
                text: error
            }).appendTo('.validation-summary');

            if (!field) return;

            field.addClass('field-error').select();
            field.bind('keypress', function(event) {
                field.removeClass('field-error');

                $('.validation-error').remove();

                $(this).unbind(event);
            });
        },

        cleanErrors: function() {
            var self = this,
                activeForm = self.getActiveForm(),
                fields =
                    activeForm === 'loadCharacter'
                        ? self.loginFields
                        : self.registerFields;

            for (var i = 0; i < fields.length; i++)
                fields[i].removeClass('field-error');

            $('.validation-error').remove();
            $('.status').remove();
        },

        getActiveForm: function() {
            return this.parchment[0].className;
        },

        isRegistering: function() {
            return this.getActiveForm() === 'createCharacter';
        },

        isGuest: function() {
            return this.guest.hasClass('active');
        },

        setGame: function(game) {
            this.game = game;
        },

        hasWorker: function() {
            return !!window.Worker;
        },

        getScaleFactor: function() {
            return 3;
        },

        getUIScale: function() {
            var width = window.innerWidth,
                height = window.innerHeight;

            return width <= 1000 ? 1 : width <= 1500 || height <= 870 ? 2 : 3;
        },

        revertLoader: function() {
            this.updateLoader('Connecting');
        },

        updateLoader: function(message) {
            var self = this;

            if (!message) {
                self.loading.html('');
                return;
            }

            var dots =
                '<span class="loader__dot">.</span><span class="loader__dot">.</span><span class="loader__dot">.</span>';
            self.loading.html(message + dots);
        },

        toggleLogin: function(toggle) {
            var self = this;

            self.revertLoader();

            self.toggleTyping(toggle);

            self.loggingIn = toggle;

            if (toggle) {
                self.loading.removeAttr('hidden');

                self.loginButton.addClass('disabled');
                self.registerButton.addClass('disabled');
            } else {
                self.loading.attr('hidden', true);

                self.loginButton.removeClass('disabled');
                self.registerButton.removeClass('disabled');
            }
        },

        toggleTyping: function(state) {
            var self = this;

            if (self.loginFields) {
                _.each(self.loginFields, function(field) {
                    field.prop('readonly', state);
                });
            }

            if (self.registerFields) {
                _.each(self.registerFields, function(field) {
                    field.prop('readOnly', state);
                });
            }
        },

        updateRange: function(obj) {
            var self = this,
                val =
                    (obj.val() - obj.attr('min')) /
                    (obj.attr('max') - obj.attr('min'));

            obj.css(
                'background-image',
                '-webkit-gradient(linear, left top, right top, ' +
                    'color-stop(' +
                    val +
                    ', #4d4d4d), ' +
                    'color-stop(' +
                    val +
                    ', #C5C5C5)' +
                    ')'
            );
        },

        updateOrientation: function() {
            this.orientation = this.getOrientation();
        },

        getOrientation: function() {
            return window.innerHeight > window.innerWidth
                ? 'portrait'
                : 'landscape';
        },

        onReady: function(callback) {
            this.readyCallback = callback;
        }
    });
});

({
    appDir: '../',
    baseUrl: 'js',
    dir: '../../client-build',
    optimize: 'uglify',
    optimizeCss: 'standard.keepLines',

    paths: {
        jquery: 'lib/jquery'
    },

    modules: [
        {
            name: 'main'
        }
    ]
});

/* global log, _, Detect, Modules */

define(function() {
    return Class.extend({
        init: function(game) {
            var self = this;

            self.game = game;

            self.audibles = {};
            self.format = 'mp3';

            self.song = null;
            self.songName = null;

            self.enabled = true;

            self.load();
        },

        load: function() {
            var self = this;

            self.music = {
                codingroom: false,
                smalltown: false,
                village: false,
                beach: false,
                spookyship: false,
                meadowofthepast: false
            };

            self.sounds = {
                loot: false,
                hit1: false,
                hit2: false,
                hurt: false,
                heal: false,
                chat: false,
                revive: false,
                death: false,
                firefox: false,
                achievement: false,
                kill1: false,
                kill2: false,
                noloot: false,
                teleport: false,
                chest: false,
                npc: false,
                'npc-end': false
            };
        },

        parse: function(path, name, channels, callback) {
            var self = this,
                fullPath = path + name + '.' + self.format,
                sound = document.createElement('audio');

            sound.addEventListener(
                'canplaythrough',
                function(e) {
                    this.removeEventListener(
                        'canplaythrough',
                        arguments.callee,
                        false
                    );

                    if (callback) callback();
                },
                false
            );

            sound.addEventListener(
                'error',
                function() {
                    log.error(
                        'The audible: ' +
                            name +
                            ' could not be loaded - unsupported extension?'
                    );

                    self.audibles[name] = null;
                },
                false
            );

            sound.preload = 'auto';
            sound.autobuffer = true;
            sound.src = fullPath;

            sound.load();

            self.audibles[name] = [sound];

            _.times(channels - 1, function() {
                self.audibles[name].push(sound.cloneNode(true));
            });

            if (name in self.music) self.music[name] = true;
            else if (name in self.sounds) self.sounds[name] = true;
        },

        play: function(type, name) {
            var self = this;

            if (!self.isEnabled() || !self.fileExists(name)) return;

            switch (type) {
                case Modules.AudioTypes.Music:
                    self.fadeOut(self.song, function() {
                        self.reset(self.song);
                    });

                    var song = self.get(name);

                    if (!song) return;

                    song.volume = 0;

                    song.play();

                    self.fadeIn(song);

                    self.song = song;

                    break;

                case Modules.AudioTypes.SFX:
                    if (!self.sounds[name])
                        self.parse('audio/sounds/', name, 4);

                    var sound = self.get(name);

                    if (!sound) return;

                    sound.volume = self.getSFXVolume();

                    sound.play();

                    break;
            }
        },

        update: function() {
            var self = this;

            if (!self.isEnabled()) return;

            var song = self.getMusic(self.songName);

            if (song && !(self.song && self.song.name === song.name)) {
                if (self.game.renderer.mobile) self.reset(self.song);
                else self.fadeSongOut();

                if (song.name in self.music && !self.music[song.name]) {
                    self.parse('audio/music/', song.name, 1);

                    var music = self.audibles[song.name][0];

                    music.loop = true;
                    music.addEventListener(
                        'ended',
                        function() {
                            music.play();
                        },
                        false
                    );
                }

                self.play(Modules.AudioTypes.Music, song.name);
            } else {
                if (self.game.renderer.mobile) self.reset(self.song);
                else self.fadeSongOut();
            }
        },

        fadeIn: function(song) {
            var self = this;

            if (!song || song.fadingIn) return;

            self.clearFadeOut(song);

            song.fadingIn = setInterval(function() {
                song.volume += 0.02;

                if (song.volume >= self.getMusicVolume() - 0.02) {
                    song.volume = self.getMusicVolume();
                    self.clearFadeIn(song);
                }
            }, 100);
        },

        fadeOut: function(song, callback) {
            var self = this;

            if (!song || song.fadingOut) return;

            self.clearFadeIn(song);

            song.fadingOut = setInterval(function() {
                song.volume -= 0.08;

                if (song.volume <= 0.08) {
                    song.volume = 0;

                    if (callback) callback(song);

                    clearInterval(song.fadingOut);
                }
            }, 100);
        },

        fadeSongOut: function() {
            var self = this;

            if (!self.song) return;

            self.fadeOut(self.song, function(song) {
                self.reset(song);
            });

            self.song = null;
        },

        clearFadeIn: function(song) {
            if (song.fadingIn) {
                clearInterval(song.fadingIn);
                song.fadingIn = null;
            }
        },

        clearFadeOut: function(song) {
            if (song.fadingOut) {
                clearInterval(song.fadingOut);
                song.fadingOut = null;
            }
        },

        reset: function(song) {
            if (!song || !song.readyState > 0) return;

            song.pause();
            song.currentTime = 0;
        },

        stop: function() {
            var self = this;

            if (!self.song) return;

            self.fadeOut(self.song, function() {
                self.reset(self.song);
                self.song = null;
            });
        },

        fileExists: function(name) {
            return name in this.music || name in this.sounds;
        },

        get: function(name) {
            var self = this;

            if (!self.audibles[name]) return null;

            var audible = _.detect(self.audibles[name], function(audible) {
                return audible.ended || audible.paused;
            });

            if (audible && audible.ended) audible.currentTime = 0;
            else audible = self.audibles[name][0];

            return audible;
        },

        getMusic: function(name) {
            return {
                sound: this.get(name),
                name: name
            };
        },

        setSongVolume: function(volume) {
            this.song.volume = volume;
        },

        getSFXVolume: function() {
            return this.game.storage.data.settings.sfx / 100;
        },

        getMusicVolume: function() {
            return this.game.storage.data.settings.music / 100;
        },

        isEnabled: function() {
            return this.game.storage.data.settings.soundEnabled && this.enabled;
        }
    });
});

/* global _ */

define(['jquery', '../renderer/bubbles/blob'], function($, Blob) {
    return Class.extend({
        init: function(game) {
            var self = this;

            self.game = game;
            self.bubbles = {};

            self.container = $('#bubbles');
        },

        create: function(id, message, duration) {
            var self = this;

            if (self.bubbles[id]) {
                self.bubbles[id].reset(self.game.time);
                $('#' + id + ' p').html(message);
            } else {
                var element = $(
                    "<div id='" +
                        id +
                        "' class='bubble'><p>" +
                        message +
                        "</p><div class='bubbleTip'></div></div>"
                );

                $(element).appendTo(self.container);

                self.bubbles[id] = new Blob(id, element, duration);

                return self.bubbles[id];
            }
        },

        setTo: function(entity) {
            var self = this;

            var bubble = self.get(entity.id);

            if (!bubble || !entity) return;

            var scale = self.game.renderer.getScale(),
                tileSize = 16 * scale,
                x = (entity.x - self.game.getCamera().x) * scale,
                width = parseInt(bubble.element.css('width')) + 24,
                offset = width / 2 - tileSize / 2,
                offsetY = -20,
                y;

            y =
                (entity.y - self.game.getCamera().y) * scale -
                tileSize * 2 -
                offsetY;

            bubble.element.css(
                'left',
                x - offset + (2 + self.game.renderer.scale) + 'px'
            );
            bubble.element.css('top', y + 'px');
        },

        update: function(time) {
            var self = this;

            _.each(self.bubbles, function(bubble) {
                var entity = self.game.entities.get(bubble.id);

                if (entity) self.setTo(entity);

                if (bubble.isOver(time)) {
                    bubble.destroy();
                    delete self.bubbles[bubble.id];
                }
            });
        },

        get: function(id) {
            var self = this;

            if (id in self.bubbles) return self.bubbles[id];

            return null;
        },

        clean: function() {
            var self = this;

            _.each(self.bubbles, function(bubble) {
                bubble.destroy();
            });

            self.bubbles = {};
        },

        destroy: function(id) {
            var self = this,
                bubble = self.get(id);

            if (!bubble) return;

            bubble.destroy();
            delete self.bubbles[id];
        }
    });
});

/* global Packets, Modules, log */

define(['jquery'], function($) {
    return Class.extend({
        init: function(game) {
            var self = this;

            self.game = game;

            self.chat = $('#chat');
            self.log = $('#chatLog');
            self.input = $('#chatInput');
            self.button = $('#chatButton');

            self.visible = false;

            self.fadingDuration = 5000;
            self.fadingTimeout = null;

            self.button.click(function() {
                self.button.blur();

                if (self.input.is(':visible')) self.hideInput();
                else self.toggle();
            });
        },

        add: function(source, text, colour) {
            var self = this,
                element = $('<p>' + source + ': ' + text + '</p>');

            self.showChat();

            if (!self.isActive()) self.hideInput();

            self.hideChat();

            element.css('color', colour || 'white');

            self.log.append(element);
            self.log.scrollTop(99999);
        },

        key: function(data) {
            var self = this;

            switch (data) {
                case Modules.Keys.Enter:
                    if (self.input.val() === '') self.toggle();
                    else self.send();

                    break;
            }
        },

        send: function() {
            var self = this;

            self.game.socket.send(Packets.Chat, [self.input.val()]);
            self.toggle();
        },

        toggle: function() {
            var self = this;

            self.clean();

            if (self.visible && !self.isActive()) self.showInput();
            else if (self.visible) {
                self.hideInput();
                self.hideChat();
            } else {
                self.showChat();
                self.showInput();
            }
        },

        showChat: function() {
            var self = this;

            self.chat.fadeIn('fast');

            self.visible = true;
        },

        showInput: function() {
            var self = this;

            self.button.addClass('active');

            self.input.fadeIn('fast');
            self.input.val('');
            self.input.focus();

            self.clean();
        },

        hideChat: function() {
            var self = this;

            if (self.fadingTimeout) {
                clearTimeout(self.fadingTimeout);
                self.fadingTimeout = null;
            }

            self.fadingTimeout = setTimeout(function() {
                if (!self.isActive()) {
                    self.chat.fadeOut('slow');

                    self.visible = false;
                }
            }, self.fadingDuration);
        },

        hideInput: function() {
            var self = this;

            self.button.removeClass('active');

            self.input.val('');
            self.input.fadeOut('fast');
            self.input.blur();

            self.hideChat();
        },

        clean: function() {
            var self = this;

            clearTimeout(self.fadingTimeout);
            self.fadingTimeout = null;
        },

        isActive: function() {
            return this.input.is(':focus');
        }
    });
});

/* global log, _, Modules, Packets */

define([
    '../renderer/grids',
    '../entity/objects/chest',
    '../entity/character/character',
    '../entity/character/player/player',
    '../entity/objects/item',
    './sprites',
    '../entity/character/mob/mob',
    '../entity/character/npc/npc',
    '../entity/objects/projectile'
], function(
    Grids,
    Chest,
    Character,
    Player,
    Item,
    Sprites,
    Mob,
    NPC,
    Projectile
) {
    return Class.extend({
        init: function(game) {
            var self = this;

            self.game = game;
            self.renderer = game.renderer;

            self.grids = null;
            self.sprites = null;

            self.entities = {};
            self.decrepit = {};
        },

        load: function() {
            var self = this;

            self.game.app.sendStatus('Loading sprites');

            if (!self.sprites) {
                self.sprites = new Sprites(self.game.renderer);

                self.sprites.onLoadedSprites(function() {
                    self.game.input.loadCursors();
                });
            }

            self.game.app.sendStatus('Loading grids');

            if (!self.grids) self.grids = new Grids(self.game.map);
        },

        update: function() {
            var self = this;

            if (self.sprites) self.sprites.updateSprites();
        },

        create: function(info) {
            var self = this,
                entity;

            if (self.isPlayer(info.id)) return;

            switch (info.type) {
                case 'chest':
                    /**
                     * Here we will parse the different types of chests..
                     * We can go Dark Souls style and implement mimics
                     * the proper way -ahem- Kaetram V1.0
                     */

                    var chest = new Chest(info.id, info.string);

                    entity = chest;

                    break;

                case 'npc':
                    var npc = new NPC(info.id, info.string);

                    entity = npc;

                    break;

                case 'item':
                    var item = new Item(
                        info.id,
                        info.string,
                        info.count,
                        info.ability,
                        info.abilityLevel
                    );

                    entity = item;

                    break;

                case 'mob':
                    var mob = new Mob(info.id, info.string);

                    mob.setHitPoints(info.hitPoints);
                    mob.setMaxHitPoints(info.maxHitPoints);

                    mob.attackRange = info.attackRange;
                    mob.level = info.level;
                    mob.hiddenName = info.hiddenName;

                    entity = mob;

                    break;

                case 'projectile':
                    var attacker = self.get(info.characterId),
                        target = self.get(info.targetId);

                    if (!attacker || !target) return;

                    attacker.lookAt(target);

                    var projectile = new Projectile(
                        info.id,
                        info.projectileType,
                        attacker
                    );

                    projectile.name = info.name;

                    projectile.setStart(attacker.x, attacker.y);
                    projectile.setTarget(target);

                    projectile.setSprite(self.getSprite(projectile.name));
                    projectile.setAnimation('travel', projectile.getSpeed());

                    projectile.angled = true;
                    projectile.type = info.type;

                    /**
                     * Move this into the external overall function
                     */

                    projectile.onImpact(function() {
                        /**
                         * The data in the projectile is only for rendering purposes
                         * there is nothing you can change for the actual damage output here.
                         */

                        if (
                            self.isPlayer(projectile.owner.id) ||
                            self.isPlayer(target.id)
                        ) {
                            self.game.socket.send(Packets.Projectile, [
                                Packets.ProjectileOpcode.Impact,
                                info.id,
                                target.id
                            ]);
                        }

                        if (info.hitType === Modules.Hits.Explosive)
                            target.explosion = true;

                        self.game.info.create(
                            Modules.Hits.Damage,
                            [info.damage, self.isPlayer(target.id)],
                            target.x,
                            target.y
                        );

                        target.triggerHealthBar();

                        self.unregisterPosition(projectile);
                        delete self.entities[projectile.getId()];
                    });

                    self.addEntity(projectile);

                    attacker.performAction(
                        attacker.orientation,
                        Modules.Actions.Attack
                    );
                    attacker.triggerHealthBar();

                    return;

                case 'player':
                    var player = new Player();

                    player.setId(info.id);
                    player.setName(info.name);
                    player.setGridPosition(info.x, info.y);

                    player.rights = info.rights;
                    player.level = info.level;
                    player.pvp = info.pvp;
                    player.pvpKills = info.pvpKills;
                    player.pvpDeaths = info.pvpDeaths;
                    player.orientation = info.orientation
                        ? info.orientation
                        : 0;
                    player.type = info.type;

                    var hitPointsData = info.hitPoints,
                        manaData = info.mana,
                        equipments = [
                            info.armour,
                            info.weapon,
                            info.pendant,
                            info.ring,
                            info.boots
                        ];

                    player.setHitPoints(hitPointsData[0]);
                    player.setMaxHitPoints(hitPointsData[1]);

                    player.setMana(manaData[0]);
                    player.setMaxMana(manaData[1]);

                    player.setSprite(self.getSprite(info.armour.string));
                    player.idle();

                    _.each(equipments, function(equipment) {
                        player.setEquipment(
                            equipment.type,
                            equipment.name,
                            equipment.string,
                            equipment.count,
                            equipment.ability,
                            equipment.abilityLevel
                        );
                    });

                    player.loadHandler(self.game);

                    self.addEntity(player);

                    return;
            }

            if (!entity) return;

            var sprite = self.getSprite(
                info.type === 'item' ? 'item-' + info.string : info.string
            );

            entity.setGridPosition(info.x, info.y);
            entity.setName(info.name);

            entity.setSprite(sprite);

            entity.setIdleSpeed(sprite.idleSpeed);

            entity.idle();
            entity.type = info.type;

            self.addEntity(entity);

            if (info.type !== 'item' && entity.handler) {
                entity.handler.setGame(self.game);
                entity.handler.load();
            }

            /**
             * Get ready for errors!
             */
        },

        isPlayer: function(id) {
            return this.game.player.id === id;
        },

        get: function(id) {
            var self = this;

            if (id in self.entities) return self.entities[id];

            return null;
        },

        exists: function(id) {
            return id in this.entities;
        },

        removeEntity: function(entity) {
            var self = this;

            self.grids.removeFromPathingGrid(entity.gridX, entity.gridY);
            self.grids.removeFromRenderingGrid(
                entity,
                entity.gridX,
                entity.gridY
            );

            delete self.entities[entity.id];
        },

        clean: function(ids) {
            var self = this;

            ids = ids[0];

            _.each(self.entities, function(entity) {
                if (ids) {
                    if (
                        ids.indexOf(parseInt(entity.id)) < 0 &&
                        entity.id !== self.game.player.id
                    )
                        self.removeEntity(entity);
                } else if (entity.id !== self.game.player.id) self.removeEntity(entity);
            });

            self.grids.resetPathingGrid();
        },

        clearPlayers: function(exception) {
            var self = this;

            _.each(self.entities, function(entity) {
                if (entity.id !== exception.id && entity.type === 'player')
                    self.removeEntity(entity);
            });

            self.grids.resetPathingGrid();
        },

        addEntity: function(entity) {
            var self = this;

            if (self.entities[entity.id]) return;

            self.entities[entity.id] = entity;
            self.registerPosition(entity);

            if (
                !(entity instanceof Item && entity.dropped) &&
                !self.renderer.isPortableDevice()
            )
                entity.fadeIn(self.game.time);
        },

        removeItem: function(item) {
            var self = this;

            if (!item) return;

            self.grids.removeFromItemGrid(item, item.gridX, item.gridY);
            self.grids.removeFromRenderingGrid(item, item.gridX, item.gridY);

            delete self.entities[item.id];
        },

        registerPosition: function(entity) {
            var self = this;

            if (!entity) return;

            if (
                entity.type === 'player' ||
                entity.type === 'mob' ||
                entity.type === 'npc' ||
                entity.type === 'chest'
            )
                self.grids.addToEntityGrid(entity, entity.gridX, entity.gridY);

            /* if (entity.type !== 'player' || entity.nonPathable)
                          self.grids.addToPathingGrid(entity.gridX, entity.gridY);*/

            if (entity.type === 'item')
                self.grids.addToItemGrid(entity, entity.gridX, entity.gridY);

            self.grids.addToRenderingGrid(entity, entity.gridX, entity.gridY);
        },

        registerDuality: function(entity) {
            var self = this;

            if (!entity) return;

            self.grids.entityGrid[entity.gridY][entity.gridX][
                entity.id
            ] = entity;

            self.grids.addToRenderingGrid(entity, entity.gridX, entity.gridY);

            if (entity.nextGridX > -1 && entity.nextGridY > -1) {
                self.grids.entityGrid[entity.nextGridY][entity.nextGridX][
                    entity.id
                ] = entity;

                /* if (!(entity instanceof Player))
                        self.grids.pathingGrid[entity.nextGridY][entity.nextGridX] = 1;*/
            }
        },

        unregisterPosition: function(entity) {
            var self = this;

            if (!entity) return;

            self.grids.removeEntity(entity);
        },

        getSprite: function(name) {
            return this.sprites.sprites[name];
        },

        getAll: function() {
            return this.entities;
        },

        forEachEntity: function(callback) {
            _.each(this.entities, function(entity) {
                callback(entity);
            });
        },

        forEachEntityAround: function(x, y, radius, callback) {
            var self = this;

            for (var i = x - radius, max_i = x + radius; i <= max_i; i++) {
                for (var j = y - radius, max_j = y + radius; j <= max_j; j++) {
                    if (self.map.isOutOfBounds(i, j)) continue;

                    _.each(self.grids.renderingGrid[j][i], function(entity) {
                        callback(entity);
                    });
                }
            }
        }
    });
});

/* global _, Modules */

define(['../utils/queue', '../renderer/infos/splat'], function(Queue, Splat) {
    return Class.extend({
        init: function(game) {
            var self = this;

            self.game = game;

            self.infos = {};
            self.destroyQueue = new Queue();
        },

        create: function(type, data, x, y) {
            var self = this;

            switch (type) {
                case Modules.Hits.Damage:
                case Modules.Hits.Stun:
                case Modules.Hits.Critical:
                    var damage = data.shift(),
                        isTarget = data.shift(),
                        dId = self.generateId(self.game.time, damage, x, y);

                    if (damage < 1 || !isInt(damage)) damage = 'MISS';

                    var hitSplat = new Splat(dId, type, damage, x, y, false),
                        dColour = isTarget
                            ? Modules.DamageColours.received
                            : Modules.DamageColours.inflicted;

                    hitSplat.setColours(dColour.fill, dColour.stroke);

                    self.addInfo(hitSplat);

                    break;

                case Modules.Hits.Heal:
                case Modules.Hits.Mana:
                case Modules.Hits.Experience:
                    var amount = data.shift(),
                        id = self.generateId(self.game.time, amount, x, y),
                        text = '+',
                        colour;

                    if (amount < 1 || !isInt(amount)) return;

                    if (type !== Modules.Hits.Experience) text = '++';

                    var splat = new Splat(id, type, text + amount, x, y, false);

                    if (type === Modules.Hits.Heal)
                        colour = Modules.DamageColours.healed;
                    else if (type === Modules.Hits.Mana)
                        colour = Modules.DamageColours.mana;
                    else if (type === Modules.Hits.Experience)
                        colour = Modules.DamageColours.exp;

                    splat.setColours(colour.fill, colour.stroke);

                    self.addInfo(splat);

                    break;

                case Modules.Hits.LevelUp:
                    var lId = self.generateId(self.game.time, '-1', x, y),
                        levelSplat = new Splat(
                            lId,
                            type,
                            'Level Up!',
                            x,
                            y,
                            false
                        ),
                        lColour = Modules.DamageColours.exp;

                    levelSplat.setColours(lColour.fill, lColour.stroke);

                    self.addInfo(levelSplat);

                    break;
            }
        },

        getCount: function() {
            return Object.keys(this.infos).length;
        },

        addInfo: function(info) {
            var self = this;

            self.infos[info.id] = info;

            info.onDestroy(function(id) {
                self.destroyQueue.add(id);
            });
        },

        update: function(time) {
            var self = this;

            self.forEachInfo(function(info) {
                info.update(time);
            });

            self.destroyQueue.forEachQueue(function(id) {
                delete self.infos[id];
            });

            self.destroyQueue.reset();
        },

        forEachInfo: function(callback) {
            _.each(this.infos, function(info) {
                callback(info);
            });
        },

        generateId: function(time, info, x, y) {
            return time + '' + Math.abs(info) + '' + x + '' + y;
        }
    });
});

/* global Modules, log, _, Detect, Packets */

define(['jquery', '../entity/animation', './chat', './overlay'], function(
    $,
    Animation,
    Chat,
    Overlay
) {
    return Class.extend({
        init: function(game) {
            var self = this;

            self.game = game;
            self.app = game.app;
            self.renderer = game.renderer;

            self.selectedCellVisible = false;
            self.previousClick = {};
            self.cursorVisible = true;
            self.targetVisible = true;
            self.selectedX = -1;
            self.selectedY = -1;

            self.cursor = null;
            self.newCursor = null;

            self.targetData = null;
            self.targetColour = null;
            self.newTargetColour = null;
            self.mobileTargetColour = 'rgba(51, 255, 0)';

            self.keyMovement = true;
            self.cursorMoved = false;

            self.previousKey = {};

            self.cursors = {};

            self.lastMousePosition = { x: 0, y: 0 };

            self.hovering = null;
            self.hoveringInstance = null; // for debugging

            self.mouse = {
                x: 0,
                y: 0
            };

            self.load();
        },

        load: function() {
            var self = this;

            /**
             * This is the animation for the target
             * cell spinner sprite (only on desktop)
             */

            self.targetAnimation = new Animation('move', 4, 0, 16, 16);
            self.targetAnimation.setSpeed(50);

            self.chatHandler = new Chat(self.game);
            self.overlay = new Overlay(self);
        },

        loadCursors: function() {
            var self = this;

            self.cursors.hand = self.game.getSprite('hand');
            self.cursors.sword = self.game.getSprite('sword');
            self.cursors.loot = self.game.getSprite('loot');
            self.cursors.target = self.game.getSprite('target');
            self.cursors.arrow = self.game.getSprite('arrow');
            self.cursors.talk = self.game.getSprite('talk');
            self.cursors.spell = self.game.getSprite('spell');
            self.cursors.bow = self.game.getSprite('bow');

            self.newCursor = self.cursors.hand;
            self.newTargetColour = 'rgba(255, 255, 255, 0.5)';

            if (self.game.isDebug()) log.info('Loaded Cursors!');
        },

        handle: function(inputType, data) {
            var self = this,
                player = self.getPlayer();

            switch (inputType) {
                case Modules.InputType.Key:
                    if (self.chatHandler.isActive()) {
                        self.chatHandler.key(data);
                        return;
                    }

                    switch (data) {
                        case Modules.Keys.W:
                        case Modules.Keys.Up:
                            self.getPlayer().moveUp = true;

                            break;

                        case Modules.Keys.A:
                        case Modules.Keys.Left:
                            self.getPlayer().moveLeft = true;

                            break;

                        case Modules.Keys.S:
                        case Modules.Keys.Down:
                            self.getPlayer().moveDown = true;

                            break;

                        case Modules.Keys.D:
                        case Modules.Keys.Right:
                            self.getPlayer().moveRight = true;

                            break;

                        case Modules.Keys.Enter:
                            self.chatHandler.toggle();

                            break;
                    }

                    break;

                case Modules.InputType.LeftClick:
                    player.disableAction = false;
                    self.keyMovement = false;

                    self.setCoords(data);
                    self.click(self.getCoords());

                    break;
            }
        },

        keyUp: function(key) {
            var self = this,
                player = self.getPlayer();

            switch (key) {
                case Modules.Keys.W:
                case Modules.Keys.Up:
                    player.moveUp = false;
                    break;

                case Modules.Keys.A:
                case Modules.Keys.Left:
                    player.moveLeft = false;
                    break;

                case Modules.Keys.S:
                case Modules.Keys.Down:
                    player.moveDown = false;
                    break;

                case Modules.Keys.D:
                case Modules.Keys.Right:
                    player.moveRight = false;
                    break;
            }

            player.disableAction = false;
        },

        keyMove: function(position) {
            var self = this,
                player = self.getPlayer();

            if (!player.hasPath()) {
                self.keyMovement = true;
                self.cursorMoved = false;

                if (self.game.isDebug()) {
                    log.info('--- keyMove ---');
                    log.info(position);
                    log.info('---------------');
                }

                self.click(position);
            }
        },

        click: function(position) {
            var self = this,
                player = self.getPlayer();

            if (player.stunned) return;

            self.setPassiveTarget();

            /**
             * It can be really annoying having the chat open
             * on mobile, and it is far harder to control.
             */

            if (
                self.renderer.mobile &&
                self.chatHandler.input.is(':visible') &&
                self.chatHandler.input.val() === ''
            )
                self.chatHandler.hideInput();

            if (
                (self.game.zoning && self.game.zoning.direction) ||
                player.disableAction
            )
                return;

            var entity = self.game.getEntityAt(
                position.x,
                position.y,
                position.x === player.gridX && position.y === player.gridY
            );

            if (entity) {
                player.disableAction = true;

                self.setAttackTarget();

                if (self.isTargetable(entity)) player.setTarget(entity);

                if (
                    player.getDistance(entity) < 7 &&
                    player.isRanged() &&
                    self.isAttackable(entity)
                ) {
                    self.game.socket.send(Packets.Target, [
                        Packets.TargetOpcode.Attack,
                        entity.id
                    ]);
                    player.lookAt(entity);
                    return;
                }

                if (
                    entity.gridX === player.gridX &&
                    entity.gridY === player.gridY
                ) {
                    self.game.socket.send(Packets.Target, [
                        Packets.TargetOpcode.Attack,
                        entity.id
                    ]);
                }

                /* if (entity.type === 'player') {
                    self.getActions().showPlayerActions(entity, self.mouse.x, self.mouse.y);
                    return;
                }*/

                if (self.isTargetable(entity)) {
                    player.follow(entity);
                    return;
                }
            } else player.removeTarget();

            self.getActions().hidePlayerActions();

            player.go(position.x, position.y);

            if (self.game.interface) self.game.interface.hideAll();

            if (!self.game.audio.song && Detect.isSafari())
                self.game.audio.update();
        },

        updateCursor: function() {
            var self = this;

            if (!self.cursorVisible) return;

            if (self.newCursor !== self.cursor) self.cursor = self.newCursor;

            if (self.newTargetColour !== self.targetColour)
                self.targetColour = self.newTargetColour;
        },

        moveCursor: function() {
            var self = this;

            if (!self.renderer || self.renderer.mobile || !self.renderer.camera)
                return;

            var position = self.getCoords(),
                player = self.getPlayer(),
                entity = self.game.getEntityAt(
                    position.x,
                    position.y,
                    player.gridX === position.x && player.gridY === position.y
                );

            self.overlay.update(entity);

            if (
                !entity ||
                entity.id === player.id ||
                entity.type === 'player'
            ) {
                self.setCursor(self.cursors.hand);
                self.hovering = null;
            } else {
                if (self.renderer.debugging) self.hoveringInstance = entity.id;

                switch (entity.type) {
                    case 'item':
                    case 'chest':
                        self.setCursor(self.cursors.loot);
                        self.hovering = Modules.Hovering.Item;
                        break;

                    case 'mob':
                        self.setCursor(self.getAttackCursor());
                        self.hovering = Modules.Hovering.Mob;
                        break;

                    case 'npc':
                        self.setCursor(self.cursors.talk);
                        self.hovering = Modules.Hovering.NPC;
                        break;
                }
            }
        },

        setPosition: function(x, y) {
            var self = this;

            self.selectedX = x;
            self.selectedY = y;
        },

        setCoords: function(event) {
            var self = this,
                offset = self.app.canvas.offset(),
                width = self.renderer.background.width,
                height = self.renderer.background.height,
                proportionality = self.renderer.scale === 3 ? 2 / 3 : 1;

            self.cursorMoved = false;

            self.mouse.x =
                Math.round(event.pageX - offset.left) * proportionality;
            self.mouse.y =
                Math.round(event.pageY - offset.top) * proportionality;

            if (self.mouse.x >= width) self.mouse.x = width - 1;
            else if (self.mouse.x <= 0) self.mouse.x = 0;

            if (self.mouse.y >= height) self.mouse.y = height - 1;
            else if (self.mouse.y <= 0) self.mouse.y = 0;
        },

        setCursor: function(cursor) {
            var self = this;

            if (cursor) self.newCursor = cursor;
            else log.error('Cursor: ' + cursor + ' could not be found.');
        },

        setAttackTarget: function() {
            var self = this;

            self.targetAnimation.setRow(1);
            self.mobileTargetColour = 'rgb(255, 51, 0)';
        },

        setPassiveTarget: function() {
            var self = this;

            self.targetAnimation.setRow(0);
            self.mobileTargetColour = 'rgb(51, 255, 0)';
        },

        getAttackCursor: function() {
            return this.cursors[this.getPlayer().isRanged() ? 'bow' : 'sword'];
        },

