define(['jquery'], function($) {
    return Class.extend({
        init: function(input) {
            var self = this;

            self.input = input;
            self.hovering = null;

            self.attackInfo = $('#attackInfo');

            self.image = self.attackInfo.find('.image div');
            self.name = self.attackInfo.find('.name');
            self.details = self.attackInfo.find('.details');
            self.health = self.attackInfo.find('.health');
        },

        update: function(entity) {
            var self = this;

            if (!self.validEntity(entity)) {
                self.hovering = null;

                if (self.isVisible()) self.hide();

                return;
            }

            if (!self.isVisible()) self.display();

            self.hovering = entity;

            self.name.html(
                entity.type === 'player' ? entity.username : entity.name
            );

            if (self.hasHealth()) {
                self.health.css({
                    display: 'block',
                    width:
                        Math.ceil(
                            (entity.hitPoints / entity.maxHitPoints) * 100
                        ) -
                        10 +
                        '%'
                });

                self.details.html(
                    entity.hitPoints + ' / ' + entity.maxHitPoints
                );
            } else {
                self.health.css('display', 'none');
                self.details.html('');
            }

            self.onUpdate(function(entityId, hitPoints) {
                if (
                    self.hovering &&
                    self.hovering.id === entityId &&
                    self.hovering.type !== 'npc' &&
                    self.hovering.type !== 'item'
                ) {
                    if (hitPoints < 1) self.hide();
                    else {
                        self.health.css(
                            'width',
                            Math.ceil(
                                (hitPoints / self.hovering.maxHitPoints) * 100
                            ) -
                                10 +
                                '%'
                        );
                        self.details.html(
                            hitPoints + ' / ' + self.hovering.maxHitPoints
                        );
                    }
                }
            });
        },

        validEntity: function(entity) {
            return (
                entity &&
                entity.id !== this.input.getPlayer().id &&
                entity.type !== 'projectile'
            );
        },

        clean: function() {
            var self = this;

            self.details.html('');
            self.hovering = null;
        },

        hasHealth: function() {
            return (
                this.hovering.type === 'mob' || this.hovering.type === 'player'
            );
        },

        display: function() {
            this.attackInfo.fadeIn('fast');
        },

        hide: function() {
            this.attackInfo.fadeOut('fast');
        },

        isVisible: function() {
            return this.attackInfo.css('display') === 'block';
        },

        getGame: function() {
            return this.input.game;
        },

        onUpdate: function(callback) {
            this.updateCallback = callback;
        }
    });
});

define(['jquery', '../renderer/pointers/pointer'], function($, Pointer) {
    return Class.extend({
        init: function(game) {
            var self = this;

            self.game = game;
            self.pointers = {};

            self.scale = self.getScale();

            self.container = $('#bubbles');
        },

        create: function(id, type, name) {
            var self = this;

            if (id in self.pointers) return;

            switch (type) {
                case Modules.Pointers.Button:
                    self.pointers[id] = new Pointer(id, $('#' + name), type);

                    break;

                default:
                    var element = $(
                        '<div id="' + id + '" class="pointer"></div>'
                    );

                    self.setSize(element);

                    self.container.append(element);

                    self.pointers[id] = new Pointer(id, element, type);

                    break;
            }
        },

        resize: function() {
            var self = this;

            _.each(self.pointers, function(pointer) {
                switch (pointer.type) {
                    case Modules.Pointers.Relative:
                        var scale = self.getScale(),
                            x = pointer.x,
                            y = pointer.y,
                            offsetX = 0,
                            offsetY = 0;

                        pointer.element.css('left', x * scale - offsetX + 'px');
                        pointer.element.css('top', y * scale - offsetY + 'px');

                        break;
                }
            });
        },

        setSize: function(element) {
            var self = this;

            element.css({
                width: 16 + 16 * self.scale + 'px',
                height: 16 + 16 * self.scale + 'px',
                margin: 'inherit',
                'margin-top': '-' + 6 * self.scale + 'px',
                top: 10 * self.scale + 'px',
                background: 'url("img/' + self.scale + '/pointer.png")'
            });
        },

        clean: function() {
            var self = this;

            _.each(self.pointers, function(pointer) {
                pointer.destroy();
            });

            self.pointers = {};
        },

        destroy: function(pointer) {
            var self = this;

            delete self.pointers[pointer.id];
            pointer.destroy();
        },

        set: function(pointer, posX, posY) {
            var self = this;

            self.updateCamera();

            var tileSize = 48, // 16 * self.scale
                x = (posX - self.camera.x) * self.scale,
                width = parseInt(pointer.element.css('width') + 24),
                offset = width / 2 - tileSize / 2,
                y,
                outX,
                outY;

            y = (posY - self.camera.y) * self.scale - tileSize;

            outX = x / self.game.renderer.canvasWidth;
            outY = y / self.game.renderer.canvasHeight;

            if (outX >= 1.5) {
                // right
                pointer.element.css('left', '');
                pointer.element.css('right', '0');
                pointer.element.css('top', '50%');
                pointer.element.css('bottom', '');

                pointer.element.css('transform', 'rotate(-90deg)');
            } else if (outY >= 1.5) {
                // bottom

                pointer.element.css('left', '50%');
                pointer.element.css('right', '');
                pointer.element.css('top', '');
                pointer.element.css('bottom', '0');

                pointer.element.css('transform', '');
            } else if (outX <= 0) {
                // left

                pointer.element.css('left', '0');
                pointer.element.css('right', '');
                pointer.element.css('top', '50%');
                pointer.element.css('bottom', '');

                pointer.element.css('transform', 'rotate(90deg)');
            } else if (outY <= 0) {
                // top

                pointer.element.css('left', '');
                pointer.element.css('right', '50%');
                pointer.element.css('top', '0');
                pointer.element.css('bottom', '');

                pointer.element.css('transform', 'rotate(180deg)');
            } else {
                pointer.element.css('left', x - offset + 'px');
                pointer.element.css('right', '');
                pointer.element.css('top', y + 'px');
                pointer.element.css('bottom', '');

                pointer.element.css('transform', '');
            }
        },

        setToEntity: function(entity) {
            var self = this,
                pointer = self.get(entity.id);

            if (!pointer) return;

            self.set(pointer, entity.x, entity.y);
        },

        setToPosition: function(id, x, y) {
            var self = this,
                pointer = self.get(id);

            if (!pointer) return;

            pointer.setPosition(x, y);

            self.set(pointer, x, y);
        },

        setRelative: function(id, x, y) {
            var self = this,
                pointer = self.get(id);

            if (!pointer) return;

            var scale = self.getScale(),
                offsetX = 0,
                offsetY = 0;

            pointer.setPosition(x, y);

            pointer.element.css('left', x * scale - offsetX + 'px');
            pointer.element.css('top', y * scale - offsetY + 'px');
        },

        update: function() {
            var self = this;

            _.each(self.pointers, function(pointer) {
                switch (pointer.type) {
                    case Modules.Pointers.Entity:
                        var entity = self.game.entities.get(pointer.id);

                        if (entity) self.setToEntity(entity);
                        else self.destroy(pointer);

                        break;

                    case Modules.Pointers.Position:
                        if (pointer.x !== -1 && pointer.y !== -1)
                            self.set(pointer, pointer.x, pointer.y);

                        break;
                }
            });
        },

        get: function(id) {
            var self = this;

            if (id in self.pointers) return self.pointers[id];

            return null;
        },

        updateCamera: function() {
            this.camera = this.game.renderer.camera;
        },

        getScale: function() {
            return this.game.getScaleFactor(); // always 3
        }
    });
});

