define([
    '../character',
    './equipment/armour',
    './equipment/weapon',
    './equipment/pendant',
    './equipment/boots',
    './equipment/ring'
], function(Character, Armour, Weapon, Pendant, Boots, Ring) {
    return Character.extend({
        init: function() {
            var self = this;

            self._super(-1, Modules.Types.Player);

            self.username = '';
            self.password = '';
            self.email = '';

            self.avatar = null;

            self.rights = 0;
            self.wanted = false;
            self.experience = -1;
            self.nextExperience = -1;
            self.prevExperience = -1;
            self.level = -1;
            self.pvpKills = -1;
            self.pvpDeaths = -1;

            self.hitPoints = -1;
            self.maxHitPoints = -1;
            self.mana = -1;
            self.maxMana = -1;

            self.prevX = 0;
            self.prevY = 0;

            self.direction = null;
            self.pvp = false;

            self.moveLeft = false;
            self.moveRight = false;
            self.moveUp = false;
            self.moveDown = false;
            self.disableAction = false;

            self.loadEquipment();
        },

        load: function(data) {
            var self = this;

            self.setId(data.instance);
            self.setGridPosition(data.x, data.y);
            self.setPointsData(data.hitPoints, data.mana);
            self.setExperience(
                data.experience,
                data.nextExperience,
                data.prevExperience
            );

            self.username = data.username;
            self.level = data.level;

            self.lastLogin = data.lastLogin;
            self.pvpKills = data.pvpKills;
            self.pvpDeaths = data.pvpDeaths;

            self.orientation = data.orienation;

            self.type = 'player';
        },

        loadHandler: function(game) {
            var self = this;

            /**
             * This is for other player characters
             */

            self.handler.setGame(game);
            self.handler.load();
        },

        hasKeyboardMovement: function() {
            return (
                this.moveLeft || this.moveRight || this.moveUp || this.moveDown
            );
        },

        stop: function(force) {
            this._super(force);
        },

        setId: function(id) {
            this.id = id;
        },

        idle: function() {
            this._super();
        },

        loadEquipment: function() {
            var self = this;

            self.armour = null;
            self.weapon = null;
            self.pendant = null;
            self.ring = null;
            self.boots = null;
        },

        isRanged: function() {
            return this.weapon && this.weapon.ranged;
        },

        follow: function(character) {
            this._super(character);
        },

        go: function(x, y, forced) {
            this._super(x, y, forced);
        },

        hasWeapon: function() {
            return this.weapon ? this.weapon.exists() : false;
        },

        performAction: function(orientation, action) {
            this._super(orientation, action);
        },

        setName: function(name) {
            var self = this;

            self.username = name;
            self.name = name;
        },

        setSprite: function(sprite) {
            this._super(sprite);
        },

        getSpriteName: function() {
            return this.armour ? this.armour.string : 'clotharmor';
        },

        setGridPosition: function(x, y) {
            this._super(x, y);
        },

        setHitPoints: function(hitPoints) {
            this._super(hitPoints);
        },

        setMaxHitPoints: function(maxHitPoints) {
            this._super(maxHitPoints);
        },

        setMana: function(mana) {
            this.mana = mana;
        },

        setMaxMana: function(maxMana) {
            this.maxMana = maxMana;
        },

        clearHealthBar: function() {
            this._super();
        },

        getX: function() {
            return this.gridX;
        },

        getY: function() {
            return this.gridY;
        },

        setExperience: function(experience, nextExperience, prevExperience) {
            var self = this;

            self.experience = experience;
            self.nextExperience = nextExperience;
            self.prevExperience = prevExperience;

            if (self.experienceCallback) self.experienceCallback();
        },

        setPointsData: function(hitPointsData, manaData) {
            var self = this,
                hitPoints = hitPointsData.shift(),
                maxHitPoints = hitPointsData.shift(),
                mana = manaData.shift(),
                maxMana = manaData.shift();

            self.setMaxHitPoints(maxHitPoints);
            self.setMaxMana(maxMana);

            self.setHitPoints(hitPoints);
            self.setMana(mana);
        },

        setEquipment: function(
            type,
            name,
            string,
            count,
            ability,
            abilityLevel
        ) {
            var self = this;

            switch (type) {
                case Modules.Equipment.Armour:
                    if (!self.armour) {
                        self.armour = new Armour(
                            name,
                            string,
                            count,
                            ability,
                            abilityLevel
                        );
                    } else {
                        self.armour.update(
                            name,
                            string,
                            count,
                            ability,
                            abilityLevel
                        );
                    }

                    if (self.updateArmourCallback)
                        self.updateArmourCallback(string);

                    break;

                case Modules.Equipment.Weapon:
                    if (!self.weapon) {
                        self.weapon = new Weapon(
                            name,
                            string,
                            count,
                            ability,
                            abilityLevel
                        );
                    } else {
                        self.weapon.update(
                            name,
                            string,
                            count,
                            ability,
                            abilityLevel
                        );
                    }

                    self.weapon.ranged = string.includes('bow');

                    break;

                case Modules.Equipment.Pendant:
                    if (!self.pendant) {
                        self.pendant = new Pendant(
                            name,
                            string,
                            count,
                            ability,
                            abilityLevel
                        );
                    } else {
                        self.pendant.update(
                            name,
                            string,
                            count,
                            ability,
                            abilityLevel
                        );
                    }

                    break;

                case Modules.Equipment.Ring:
                    if (!self.ring) {
                        self.ring = new Ring(
                            name,
                            string,
                            count,
                            ability,
                            abilityLevel
                        );
                    } else {
                        self.ring.update(
                            name,
                            string,
                            count,
                            ability,
                            abilityLevel
                        );
                    }

                    break;

                case Modules.Equipment.Boots:
                    if (!self.boots) {
                        self.boots = new Boots(
                            name,
                            string,
                            count,
                            ability,
                            abilityLevel
                        );
                    } else {
                        self.boots.update(
                            name,
                            string,
                            count,
                            ability,
                            abilityLevel
                        );
                    }

                    break;
            }
        },

        unequip: function(type) {
            var self = this;

            switch (type) {
                case 'armour':
                    self.armour.update('Cloth Armour', 'clotharmor', 1, -1, -1);
                    break;

                case 'weapon':
                    self.weapon.update(null, null, -1, -1, -1);
                    break;

                case 'pendant':
                    self.pendant.update(null, null, -1, -1, -1);
                    break;

                case 'ring':
                    self.ring.update(null, null, -1, -1, -1);
                    break;

                case 'boots':
                    self.boots.update(null, null, -1, -1, -1);
                    break;
            }
        },

        tempBlink: function() {
            var self = this;

            self.blink(90);

            if (!self.tempBlinkTimeout) {
                self.tempBlinkTimeout = setTimeout(function() {
                    self.stopBlinking();
                }, 500);
            }
        },

        getDistance: function(entity) {
            return this._super(entity);
        },

        onUpdateArmour: function(callback) {
            this.updateArmourCallback = callback;
        },

        onExperience: function(callback) {
            this.experienceCallback = callback;
        }
    });
});

/* global log, Packets, Modules */

define(function() {
    /**
     * This is a player handler, responsible for all the callbacks
     * without having to clutter up the entire game file.
     */

    return Class.extend({
        init: function(game, player) {
            var self = this;

            self.game = game;
            self.camera = game.getCamera();
            self.input = game.input;
            self.player = player;
            self.entities = game.entities;
            self.socket = game.socket;
            self.renderer = game.renderer;

            self.load();
        },

        load: function() {
            var self = this;

            self.player.onRequestPath(function(x, y) {
                if (self.player.dead) return null;

                var ignores = [self.player];

                if (self.player.hasTarget()) ignores.push(self.player.target);

                if (!self.game.map.isColliding(x, y)) {
                    self.socket.send(Packets.Movement, [
                        Packets.MovementOpcode.Request,
                        x,
                        y,
                        self.player.gridX,
                        self.player.gridY
                    ]);
                }

                return self.game.findPath(self.player, x, y, ignores);
            });

            self.player.onStartPathing(function(path) {
                var i = path.length - 1;

                self.input.selectedX = path[i][0];
                self.input.selectedY = path[i][1];
                self.input.selectedCellVisible = true;

                if (
                    !self.game.getEntityAt(
                        self.input.selectedX,
                        self.input.selectedY
                    )
                ) {
                    self.socket.send(Packets.Target, [
                        Packets.TargetOpcode.None
                    ]);
                }

                self.socket.send(Packets.Movement, [
                    Packets.MovementOpcode.Started,
                    self.input.selectedX,
                    self.input.selectedY,
                    self.player.gridX,
                    self.player.gridY
                ]);
            });

            self.player.onStopPathing(function(x, y) {
                self.entities.unregisterPosition(self.player);
                self.entities.registerPosition(self.player);

                self.input.selectedCellVisible = false;

                self.camera.clip();

                var id = null,
                    entity = self.game.getEntityAt(x, y, true);

                if (entity) id = entity.id;

                var hasTarget = self.player.hasTarget();

                self.socket.send(Packets.Movement, [
                    Packets.MovementOpcode.Stop,
                    x,
                    y,
                    id,
                    hasTarget,
                    self.player.orientation
                ]);

                if (hasTarget) {
                    self.socket.send(Packets.Target, [
                        self.isAttackable()
                            ? Packets.TargetOpcode.Attack
                            : Packets.TargetOpcode.Talk,
                        self.player.target.id
                    ]);

                    self.player.lookAt(self.player.target);
                }

                self.input.setPassiveTarget();

                self.game.storage.setOrientation(self.player.orientation);
            });

            self.player.onBeforeStep(function() {
                self.entities.unregisterPosition(self.player);

                if (!self.isAttackable()) return;

                if (self.player.isRanged()) {
                    if (self.player.getDistance(self.player.target) < 7)
                        self.player.stop();
                } else {
                    self.input.selectedX = self.player.target.gridX;
                    self.input.selectedY = self.player.target.gridY;
                }
            });

            self.player.onStep(function() {
                if (self.player.hasNextStep())
                    self.entities.registerDuality(self.player);

                if (
                    !self.camera.centered ||
                    self.camera.lockX ||
                    self.camera.lockY
                )
                    self.checkBounds();

                self.player.forEachAttacker(function(attacker) {
                    if (!attacker.stunned) attacker.follow(self.player);
                });

                self.socket.send(Packets.Movement, [
                    Packets.MovementOpcode.Step,
                    self.player.gridX,
                    self.player.gridY
                ]);
            });

            self.player.onSecondStep(function() {
                self.renderer.updateAnimatedTiles();
            });

            self.player.onMove(function() {
                /**
                 * This is a callback representing the absolute exact position of the player.
                 */

                if (self.camera.centered) self.camera.centreOn(self.player);

                if (self.player.hasTarget())
                    self.player.follow(self.player.target);
            });

            self.player.onUpdateArmour(function(armourName) {
                self.player.setSprite(self.game.getSprite(armourName));
            });
        },

        isAttackable: function() {
            var self = this,
                target = self.player.target;

            if (!target) return;

            return (
                target.type === 'mob' ||
                (target.type === 'player' && target.pvp)
            );
        },

        checkBounds: function() {
            var self = this,
                x = self.player.gridX - self.camera.gridX,
                y = self.player.gridY - self.camera.gridY,
                isBorder = false;

            if (x === 0) self.game.zoning.setLeft();
            else if (y === 0) self.game.zoning.setUp();
            else if (x === self.camera.gridWidth - 1)
                self.game.zoning.setRight();
            else if (y === self.camera.gridHeight - 1)
                self.game.zoning.setDown();

            if (self.game.zoning.direction !== null) {
                self.camera.zone(self.game.zoning.getDirection());
                self.game.zoning.reset();
            }
        }
    });
});

