            self.started = true;
        },

        stop: function() {
            var self = this;

            self.stopped = false;
            self.started = false;
            self.ready = false;
        },

        tick: function() {
            var self = this;

            if (self.ready) {
                self.time = new Date().getTime();

                self.renderer.render();
                self.updater.update();

