define(['jquery'], function($) {
    return Class.extend({
        init: function(game, intrface) {
            var self = this;

            self.game = game;
            self.interface = intrface;

            self.body = $('#enchant');
            self.container = $('#enchantContainer');
            self.enchantSlots = $('#enchantInventorySlots');

            self.selectedItem = $('#enchantSelectedItem');
            self.selectedShards = $('#enchantShards');
            self.confirm = $('#confirmEnchant');
            self.shardsCount = $('#shardsCount');

            self.confirm.css({
                left: '70%',
                top: '80%'
            });

            $('#closeEnchant').click(function() {
                self.hide();
            });

            self.confirm.click(function() {
                self.enchant();
            });
        },

        resize: function() {
            var self = this;

            self.load();
        },

        load: function() {
            var self = this,
                list = self.getSlots(),
                inventoryList = self.interface.bank.getInventoryList();

            list.empty();

            for (var i = 0; i < self.getInventorySize(); i++) {
                var item = $(inventoryList[i]).clone(),
                    slot = item.find('#bankInventorySlot' + i);

                slot.click(function(event) {
                    self.select(event);
                });

                list.append(item);
            }

            self.selectedItem.click(function() {
                self.remove('item');
            });

            self.selectedShards.click(function() {
                self.remove('shards');
            });
        },

        add: function(type, index) {
            var self = this,
                image = self.getSlot(index).find('#inventoryImage' + index);

            switch (type) {
                case 'item':
                    self.selectedItem.css(
                        'background-image',
                        image.css('background-image')
                    );

                    if (Detect.isMobile())
                        self.selectedItem.css('background-size', '600%');

                    break;

                case 'shards':
                    self.selectedShards.css(
                        'background-image',
                        image.css('background-image')
                    );

                    if (Detect.isMobile())
                        self.selectedShards.css('background-size', '600%');

                    var count = self.getItemSlot(index).count;

                    if (count > 1) self.shardsCount.text(count);

                    break;
            }

            image.css('background-image', '');

            self.getSlot(index)
                .find('#inventoryItemCount' + index)
                .text('');
        },

        moveBack: function(type, index) {
            var self = this,
                image = self.getSlot(index).find('#inventoryImage' + index),
                itemCount = self
                    .getSlot(index)
                    .find('#inventoryItemCount' + index),
                count = self.getItemSlot(index).count;

            switch (type) {
                case 'item':
                    image.css(
                        'background-image',
                        self.selectedItem.css('background-image')
                    );

                    if (count > 1) itemCount.text(count);

                    self.selectedItem.css('background-image', '');

                    break;

                case 'shards':
                    image.css(
                        'background-image',
                        self.selectedShards.css('background-image')
                    );

                    if (count > 1) itemCount.text(count);

                    self.selectedShards.css('background-image', '');

                    self.shardsCount.text('');

                    break;
            }
        },

        enchant: function() {
            this.game.socket.send(Packets.Enchant, [
                Packets.EnchantOpcode.Enchant
            ]);
        },

        select: function(event) {
            this.game.socket.send(Packets.Enchant, [
                Packets.EnchantOpcode.Select,
                event.currentTarget.id.substring(17)
            ]);
        },

        remove: function(type) {
            this.game.socket.send(Packets.Enchant, [
                Packets.EnchantOpcode.Remove,
                type
            ]);
        },

        getInventorySize: function() {
            return this.interface.inventory.getSize();
        },

        getItemSlot: function(index) {
            return this.interface.inventory.container.slots[index];
        },

        display: function() {
            var self = this;

            self.body.fadeIn('fast');
            self.load();
        },

        hide: function() {
            var self = this;

            self.selectedItem.css('background-image', '');
            self.selectedShards.css('background-image', '');

            self.body.fadeOut('fast');
        },

        hasImage: function(image) {
            return image.css('background-image') !== 'none';
        },

        getSlot: function(index) {
            return $(this.getSlots().find('li')[index]);
        },

        getSlots: function() {
            return this.enchantSlots.find('ul');
        },

        isVisible: function() {
            return this.body.css('display') === 'block';
        }
    });
});

define(['jquery', './container/container'], function($, Container) {
    return Class.extend({
        init: function(game, intrface) {
            var self = this;

            self.game = game;
            self.player = game.player;

            self.health = $('#health');
            self.healthBar = $('#healthBar');
            self.healthBarText = $('#healthBarText');

            self.exp = $('#exp');
            self.expBar = $('#expBar');

            self.load();
        },

        load: function() {
            var self = this;

            self.player.onHitPoints(function() {
                self.calculateHealthBar();
            });

            self.player.onMaxHitPoints(function() {
                self.calculateHealthBar();
            });

            self.player.onExperience(function() {
                self.calculateExpBar();
            });
        },

        calculateHealthBar: function() {
            var self = this,
                scale = self.getScale(),
                width = self.healthBar.width();

            if (scale < 2) scale = 2;

            // 11 is due to the offset of the #health in the #healthBar
            var diff = Math.floor(
                    width * (self.player.hitPoints / self.player.maxHitPoints) -
                        11 * scale
                ),
                prevWidth = self.health.width();

            if (prevWidth > diff) {
                self.health.addClass('white');

                setTimeout(function() {
                    self.health.removeClass('white');
                }, 500);
            } else if (diff - 1 > prevWidth) {
                self.health.addClass('green');

                setTimeout(function() {
                    self.health.removeClass('green');
                }, 500);
            }

            self.health.css('width', diff + 'px');
            self.healthBarText.text(
                self.player.hitPoints + '/' + self.player.maxHitPoints
            );
        },

        calculateExpBar: function() {
            var self = this,
                scale = self.getScale(),
                width = self.expBar.width();

            if (scale < 2) scale = 2;

            var experience =
                    self.player.experience - self.player.prevExperience,
                nextExperience =
                    self.player.nextExperience - self.player.prevExperience;

            var diff = Math.floor(width * (experience / nextExperience));

            self.exp.css('width', diff + 'px');
        },

        resize: function() {
            var self = this;

            self.calculateHealthBar();
            self.calculateExpBar();
        },

        getScale: function() {
            return this.game.app.getUIScale();
        }
    });
});

