        load: function() {
            var self = this;

            self.button.click(function() {
                self.game.interface.hideAll();
                self.settings.hide();

                if (self.isVisible()) {
                    self.hide();
                    self.button.removeClass('active');
                } else {
                    self.show();
                    self.button.addClass('active');
                }

                if (!self.activePage.loaded) self.activePage.load();

                self.game.socket.send(Packets.Click, [
                    'profile',
                    self.button.hasClass('active')
                ]);
            });

            self.next.click(function() {
                if (self.activeIndex + 1 < self.pages.length)
                    self.setPage(self.activeIndex + 1);
                else self.next.removeClass('enabled');
            });

            self.previous.click(function() {
                if (self.activeIndex > 0) self.setPage(self.activeIndex - 1);
                else self.previous.removeClass('enabled');
            });

            self.state = new State(self.game);
            self.ability = new Ability(self.game);
            self.settings = new Settings(self.game);
            self.quests = new Quest(self.game);

            self.pages.push(self.state, self.quests, self.ability);

            self.activePage = self.state;

            if (
                self.activeIndex === 0 &&
                self.activeIndex !== self.pages.length
            )
                self.next.addClass('enabled');
        },

        update: function() {
            var self = this;

            _.each(self.pages, function(page) {
                page.update();
            });
        },

        resize: function() {
            var self = this;

            _.each(self.pages, function(page) {
                page.resize();
            });
        },

        setPage: function(index) {
            var self = this,
                page = self.pages[index];

            self.clear();

            if (page.isVisible()) return;

            self.activePage = page;
            self.activeIndex = index;

            if (self.activeIndex + 1 === self.pages.length)
                self.next.removeClass('enabled');
            else if (self.activeIndex === 0)
                self.previous.removeClass('enabled');
            else {
                self.previous.addClass('enabled');
                self.next.addClass('enabled');
            }

            page.show();
        },

        show: function() {
            var self = this;

            self.body.fadeIn('slow');
            self.button.addClass('active');
        },

        hide: function() {
            var self = this;

            self.body.fadeOut('fast');
            self.button.removeClass('active');

            if (self.settings) self.settings.hide();
        },

        isVisible: function() {
            return this.body.css('display') === 'block';
        },

        clear: function() {
            var self = this;

            if (self.activePage) self.activePage.hide();
        }
    });
});

define(['jquery', './container/container'], function($, Container) {
    return Class.extend({
        init: function(game, intrface) {
            var self = this;

            self.game = game;

            self.body = $('#shop');
            self.shop = $('#shopContainer');
            self.inventory = $('#shopInventorySlots');

            self.player = game.player;
            self.interface = intrface;

            self.container = null;
            self.data = null;

            self.openShop = -1;

            self.items = [];
            self.counts = [];

            self.close = $('#closeShop');

            self.close.css('left', '97%');
            self.close.click(function() {
                self.hide();
            });
        },

        buy: function(event) {
            var self = this,
                id = event.currentTarget.id.substring(11);

            self.game.socket.send(Packets.Shop, [
                Packets.ShopOpcode.Buy,
                self.openShop,
                id,
                1
            ]);
        },

        sell: function() {
            var self = this;
        },

        /**
         * The shop file is already built to support full de-initialization of objects when
         * we receive an update about the stocks. So we just use that whenever we want to resize.
         * This is just a temporary fix, in reality, we do not want anyone to actually see the shop
         * do a full refresh when they buy an item or someone else buys an item.
         */

        resize: function() {
            var self = this;

            self.getInventoryList().empty();
            self.getShopList().empty();

            self.update(self.data);
        },

        update: function(data) {
            var self = this;

            self.reset();

            self.container = new Container(data.strings.length);

            // Update the global data to current revision
            self.data = data;

            self.load();
        },

        load: function() {
            var self = this;

            for (var i = 0; i < self.container.size; i++) {
                var shopItem = $(
                        '<div id="shopItem' + i + '" class="shopItem"></div>'
                    ),
                    string = self.data.strings[i],
                    name = self.data.names[i],
                    count = self.data.counts[i],
                    itemImage,
                    itemCount,
                    itemName,
                    itemBuy;

                if (!string || !name || !count) continue;

                itemImage = $(
                    '<div id="shopItemImage' +
                        i +
                        '" class="shopItemImage"></div>'
                );
                itemCount = $(
                    '<div id="shopItemCount' +
                        i +
                        '" class="shopItemCount"></div>'
                );
                itemName = $(
                    '<div id="shopItemName' +
                        i +
                        '" class="shopItemName"></div>'
                );
                itemBuy = $(
                    '<div id="shopItemBuy' + i + '" class="shopItemBuy"></div>'
                );

                itemImage.css(
                    'background-image',
                    self.container.getImageFormat(1, string)
                );
                itemCount.html(count);
                itemName.html(name);
                itemBuy.html('Purchase');

                self.container.setSlot(i, {
                    string: string,
                    count: count
                });

                // Bind the itemBuy to the local buy function.
                itemBuy.click(function(event) {
                    self.buy(event);
                });

                var listItem = $('<li></li>');

                shopItem.append(itemImage, itemCount, itemName, itemBuy);

                listItem.append(shopItem);

                self.getShopList().append(listItem);
            }

            var inventoryItems = self.interface.bank.getInventoryList(),
                inventorySize = self.interface.inventory.getSize();

            for (var j = 0; j < inventorySize; j++) {
                var item = $(inventoryItems[j]).clone(),
                    slot = item.find('#bankInventorySlot' + j);

                self.getInventoryList().append(slot);
            }
        },

        reset: function() {
            var self = this;

            self.items = [];
            self.counts = [];

            self.container = null;

            self.getShopList().empty();
            self.getInventoryList().empty();
        },

        open: function(id) {
            var self = this;

            if (!id) return;

            self.openShop = id;

            self.body.fadeIn('slow');
        },

        hide: function() {
            var self = this;

            self.openShop = -1;

            self.body.fadeOut('fast');
        },

        getScale: function() {
            return this.game.renderer.getScale();
        },

        isVisible: function() {
            return this.body.css('display') === 'block';
        },

        isShopOpen: function(shopId) {
            return this.isVisible() && this.openShop === shopId;
        },

        getShopList: function() {
            return this.shop.find('ul');
        },

        getInventoryList: function() {
            return this.inventory.find('ul');
        }
    });
});

/* global _, Modules */

define(['jquery'], function($) {
    return Class.extend({
        init: function(game) {
            var self = this;

            self.game = game;

            self.mapFrame = $('#mapFrame');
            self.button = $('#warpButton');
            self.close = $('#closeMapFrame');

            self.warpCount = 0;

            self.load();
        },

        load: function() {
            var self = this;

            self.button.click(function() {
                self.game.interface.hideAll();

                self.toggle();

                self.game.socket.send(Packets.Click, [
                    'warp',
                    self.button.hasClass('active')
                ]);
            });

            self.close.click(function() {
                self.hide();
            });

            for (var i = 1; i < 7; i++) {
                var warp = self.mapFrame.find('#warp' + i);

                if (warp) {
                    warp.click(function(event) {
                        self.hide();

                        self.game.socket.send(Packets.Warp, [
                            event.currentTarget.id.substring(4)
                        ]);
                    });
                }
            }
        },

        toggle: function() {
            var self = this;

            /**
             * Just so it fades out nicely.
             */

            if (self.isVisible()) self.hide();
            else self.display();
        },

        isVisible: function() {
            return this.mapFrame.css('display') === 'block';
        },

        display: function() {
            this.mapFrame.fadeIn('slow');
            this.button.addClass('active');
        },

        hide: function() {
            this.mapFrame.fadeOut('fast');
            this.button.removeClass('active');
        }
    });
});

/* global log, Detect */

define(['jquery', './app', './game'], function($, App, Game) {
    var app, body, chatInput, game;

    var install = function() {
        if (deferredPrompt) {
            deferredPrompt.prompt();
            deferredPrompt.userChoice.then(function(choiceResult) {
                if (choiceResult.outcome === 'accepted')
                    log.info('Your PWA has been installed');
                else log.info('User chose to not install your PWA');

                deferredPrompt = null;
            });
        }
    };
    var load = function() {
        $(document).ready(function() {
            app = new App();
            body = $('body');
            chatInput = $('#chatInput');

            addClasses();
            initGame();
        });

        // This is the "Offline page" service worker

        // Add this below content to your HTML page, or add the js file to your page at the very top to register service worker

        // Check compatibility for the browser we're running this in
        if ('serviceWorker' in navigator) {
            if (navigator.serviceWorker.controller) {
                log.info(
                    '[PWA Builder] active service worker found, no need to register'
                );
            } else {
                // Register the service worker
                navigator.serviceWorker
                    .register('sw.js', {
                        scope: '../'
                    })
                    .then(function(reg) {
                        log.info(
                            '[PWA Builder] Service worker has been registered for scope: ' +
                                reg.scope
                        );
                    });
            }
        }

        window.addEventListener('beforeinstallprompt', function(e) {
            // Prevent Chrome 67 and earlier from automatically showing the prompt
            e.preventDefault();

            // Stash the event so it can be triggered later.
            deferredPrompt = e;

            install();
        });
    };

    var addClasses = function() {
        var self = this;

        if (Detect.isWindows()) body.addClass('windows');

        if (Detect.isOpera()) body.addClass('opera');

        if (Detect.isFirefoxAndroid()) chatInput.removeAttr('placeholder');
    };

    var initGame = function() {
        app.onReady(function() {
            app.sendStatus('Loading game');

            if (app.config.debug) log.info('Loading the main application...');

            game = new Game(app);
            app.setGame(game);
        });
    };

    load();
});

/* global log, _ */

define(function() {
    return Class.extend({
        init: function(game) {
            let self = this;

            self.game = game;
            self.app = game.app;
            self.audio = game.audio;
            self.messages = game.messages;
            self.storage = game.storage;
            self.socket = game.socket;
            self.input = game.input;
            self.interface = game.interface;
            self.entities = game.entities;
            self.map = game.map;
            self.overlays = game.overlays;
            self.renderer = game.renderer;
            self.bubble = game.bubble;
            self.info = game.info;
            self.pointer = game.pointer;
            self.inventory = game.inventory;

            self.load();
        },

        load: function() {
            let self = this;

            self.messages.onHandshake(function(data) {
                self.game.id = data.id;
                self.game.development = data.development;

                self.game.ready = true;

                if (!self.game.player) self.game.createPlayer();

                if (!self.map) self.game.loadMap();

                self.app.updateLoader('Logging in');

                if (self.app.isRegistering()) {
                    var registerInfo = self.app.registerFields,
                        username = registerInfo[0].val(),
                        password = registerInfo[1].val(),
                        email = registerInfo[3].val();

                    self.socket.send(Packets.Intro, [
                        Packets.IntroOpcode.Register,
                        username,
                        password,
                        email
                    ]);
                } else if (self.app.isGuest()) {
                    self.socket.send(Packets.Intro, [
                        Packets.IntroOpcode.Guest,
                        'n',
                        'n',
                        'n'
                    ]);
                } else {
                    var loginInfo = self.app.loginFields,
                        name = loginInfo[0].val(),
                        pass = loginInfo[1].val();

                    self.socket.send(Packets.Intro, [
                        Packets.IntroOpcode.Login,
                        name,
                        pass
                    ]);

                    if (self.game.hasRemember()) {
                        self.storage.data.player.username = name;
                        self.storage.data.player.password = pass;
                    } else {
                        self.storage.data.player.username = '';
                        self.storage.data.player.password = '';
                    }

                    self.storage.save();
                }
            });

            self.messages.onWelcome(function(data) {
                self.interface.loadHeader();

                self.game.player.load(data);

                self.game.start();
                self.game.postLoad();
            });

            self.messages.onEquipment(function(opcode, info) {
                switch (opcode) {
                    case Packets.EquipmentOpcode.Batch:
                        _.each(info, function(data) {
                            self.game.player.setEquipment(
                                data.type,
                                data.name,
                                data.string,
                                data.count,
                                data.ability,
                                data.abilityLevel
                            );
                        });

                        self.interface.loadProfile();

                        break;

                    case Packets.EquipmentOpcode.Equip:
                        self.game.player.setEquipment(
                            info.type,
                            info.name,
                            info.string,
                            info.count,
                            info.ability,
                            info.abilityLevel
                        );

                        self.interface.profile.update();

                        break;

                    case Packets.EquipmentOpcode.Unequip:
                        var type = info.shift();

                        self.game.player.unequip(type);

                        if (type === 'armour') {
                            self.game.player.setSprite(
                                self.game.getSprite(
                                    self.game.player.getSpriteName()
                                )
                            );
                        }

                        self.interface.profile.update();

                        break;
                }
            });

            self.messages.onSpawn(function(data) {
                self.entities.create(data.shift());
            });

            self.messages.onEntityList(function(data) {
                var ids = _.pluck(self.entities.getAll(), 'id'),
                    known = _.intersection(ids, data),
                    newIds = _.difference(data, known);

                self.entities.decrepit = _.reject(
                    self.entities.getAll(),
                    function(entity) {
                        return (
                            _.include(known, entity.id) ||
                            entity.id === self.game.player.id
                        );
                    }
                );

                self.entities.clean(newIds);

                self.socket.send(Packets.Who, newIds);
            });

            self.messages.onSync(function(data) {
                var entity = self.entities.get(data.id);

                if (!entity || entity.type !== 'player') return;

                if (data.hitPoints) {
                    entity.setHitPoints(data.hitPoints);
                    entity.setMaxHitPoints(data.maxHitPoints);
                }

                if (data.mana) {
                    entity.mana = data.mana;
                    entity.maxMana = data.maxMana;
                }

                if (data.experience) {
                    entity.experience = data.experience;
                    entity.level = data.level;
                }

                if (data.armour)
                    entity.setSprite(self.game.getSprite(data.armour));

                if (data.weapon) {
                    entity.setEquipment(
                        data.weapon.type,
                        data.weapon.name,
                        data.weapon.string,
                        data.weapon.count,
                        data.weapon.ability,
                        data.weapon.abilityLevel
                    );
                }

                self.interface.profile.update();
            });

            self.messages.onMovement(function(opcode, info) {
                switch (opcode) {
                    case Packets.MovementOpcode.Move:
                        var entity = self.entities.get(info.id);

                        if (!entity) return;

                        if (info.forced) entity.stop(true);

                        entity.go(info.x, info.y);

                        break;

                    case Packets.MovementOpcode.Follow:
                        var follower = self.entities.get(info.attackerId),
                            followee = self.entities.get(info.targetId);

                        if (!followee || !follower) return;

                        follower.follow(followee);

                        break;

                    case Packets.MovementOpcode.Stop:
                        var sEntity = self.entities.get(info.id),
                            force = info.force;

                        if (!sEntity) return;

                        sEntity.stop(force);

                        break;

                    case Packets.MovementOpcode.Freeze:
                    case Packets.MovementOpcode.Stunned:
                        var pEntity = self.entities.get(info.id);

                        if (!pEntity) return;

                        if (info.state) pEntity.stop(false);

                        if (opcode === Packets.MovementOpcode.Stunned)
                            pEntity.stunned = info.state;
                        else if (opcode === Packets.MovementOpcode.Freeze)
                            pEntity.frozen = info.state;

                        break;

                    case Packets.MovementOpcode.Orientate:
                        var player = info.shift(),
                            orientation = info.shift(),
                            entity = self.entities.get(player);

                        // entity.stop();
                        entity.performAction(
                            orientation,
                            Modules.Actions.Orientate
                        );

                        break;
                }
            });

            self.messages.onTeleport(function(info) {
                var entity = self.entities.get(info.id),
                    isPlayer = info.id === self.game.player.id;

                if (!entity) return;

                entity.stop(true);
                entity.frozen = true;

                self.bubble.clean();

                /**
                 * Teleporting an entity seems to cause a glitch with the
                 * hitbox. Make sure you keep an eye out for this.
                 */

                var doTeleport = function() {
                    self.entities.unregisterPosition(entity);
                    entity.setGridPosition(info.x, info.y);

                    if (isPlayer) {
                        self.entities.clearPlayers(self.game.player);
                        self.game.player.clearHealthBar();
                        self.renderer.camera.centreOn(entity);
                        self.renderer.updateAnimatedTiles();
                    } else if (entity.type === 'player') {
                        delete self.entities.entities[entity.id];
                        return;
                    }

                    self.socket.send(Packets.Request, [self.game.player.id]);

                    self.entities.registerPosition(entity);
                    entity.frozen = false;

                    /* self.renderer.transition(15, true, function() {

                    });*/
                };

                if (info.withAnimation) {
                    var originalSprite = entity.sprite;

                    entity.teleporting = true;

                    entity.setSprite(self.game.getSprite('death'));

                    entity.animate('death', 240, 1, function() {
                        doTeleport();

                        entity.currentAnimation = null;

                        entity.setSprite(originalSprite);
                        entity.idle();

                        entity.teleporting = false;
                    });
                } else doTeleport();
                /* self.renderer.transition(15, false, function() {
                        if (self.queueColour) {
                            self.renderer.updateDarkMask(self.queueColour);
                            self.queueColour = null;
                        }
                    });*/
            });

            self.messages.onDespawn(function(id) {
                var entity = self.entities.get(id);

                if (!entity) return;

                entity.dead = true;

                entity.stop();

                switch (entity.type) {
                    case 'item':
                        self.entities.removeItem(entity);

                        return;

                    case 'chest':
                        entity.setSprite(self.game.getSprite('death'));

                        entity.setAnimation('death', 120, 1, function() {
                            self.entities.unregisterPosition(entity);
                            delete self.entities.entities[entity.id];
                        });

                        return;
                }

                if (
                    self.game.player.hasTarget() &&
                    self.game.player.target.id === entity.id
                )
                    self.game.player.removeTarget();

                self.entities.grids.removeFromPathingGrid(
                    entity.gridX,
                    entity.gridY
                );

                if (
                    entity.id !== self.game.player.id &&
                    self.game.player.getDistance(entity) < 5
                ) {
                    self.audio.play(
                        Modules.AudioTypes.SFX,
                        'kill' + Math.floor(Math.random() * 2 + 1)
                    );
                }

                entity.hitPoints = 0;

                entity.setSprite(self.game.getSprite('death'));

                entity.animate('death', 120, 1, function() {
                    self.entities.unregisterPosition(entity);
                    delete self.entities.entities[entity.id];
                });
            });

            self.messages.onCombat(function(opcode, info) {
                var attacker = self.entities.get(info.attackerId),
                    target = self.entities.get(info.targetId);

                if (!target || !attacker) return;

                switch (opcode) {
                    case Packets.CombatOpcode.Initiate:
                        attacker.setTarget(target);

                        target.addAttacker(attacker);

                        if (
                            target.id === self.game.player.id ||
                            attacker.id === self.game.player.id
                        ) {
                            self.socket.send(Packets.Combat, [
                                Packets.CombatOpcode.Initiate,
                                attacker.id,
                                target.id
                            ]);
                        }

                        break;

                    case Packets.CombatOpcode.Hit:
                        var hit = info.hitInfo,
                            isPlayer = target.id === self.game.player.id;

                        if (!hit.isAoE) {
                            attacker.lookAt(target);
                            attacker.performAction(
                                attacker.orientation,
                                Modules.Actions.Attack
                            );
                        } else if (hit.hasTerror) target.terror = true;

                        switch (hit.type) {
                            case Modules.Hits.Critical:
                                target.critical = true;

                                break;

                            default:
                                if (
                                    attacker.id === self.game.player.id &&
                                    hit.damage > 0
                                ) {
                                    self.audio.play(
                                        Modules.AudioTypes.SFX,
                                        'hit' +
                                            Math.floor(Math.random() * 2 + 1)
                                    );
                                }

                                break;
                        }

                        self.info.create(
                            hit.type,
                            [hit.damage, isPlayer],
                            target.x,
                            target.y
                        );

                        if (target.hurtSprite) {
                            target.sprite = target.hurtSprite;
                            setTimeout(function() {
                                target.sprite = target.normalSprite;
                            }, 75);
                        }

                        attacker.triggerHealthBar();
                        target.triggerHealthBar();

                        if (isPlayer && hit.damage > 0)
                            self.audio.play(Modules.AudioTypes.SFX, 'hurt');

                        break;

                    case Packets.CombatOpcode.Finish:
                        if (target) {
                            target.removeTarget();
                            target.forget();
                        }

                        if (attacker) attacker.removeTarget();

                        break;

                    case Packets.CombatOpcode.Sync:
                        if (target.x !== info.x || target.y !== info.y)
                            target.go(info.x, info.y);

                        break;
                }
            });

            self.messages.onAnimation(function(id, info) {
                var entity = self.entities.get(id),
                    animation = info.shift(),
                    speed = info.shift(),
                    count = info.shift();

                if (!entity) return;

                entity.animate(animation, speed, count);
            });

            self.messages.onProjectile(function(opcode, info) {
                switch (opcode) {
                    case Packets.ProjectileOpcode.Create:
                        self.entities.create(info);

                        break;
                }
            });

            self.messages.onPopulation(function(population) {
                self.population = population;
            });

            self.messages.onPoints(function(data) {
                var entity = self.entities.get(data.id);

                // var id = data.shift(),
                //    hitPoints = data.shift(),
                //    mana = data.shift(),
                //    entity = self.entities.get(id);

                if (!entity) return;

                if (data.hitPoints) {
                    entity.setHitPoints(data.hitPoints);

                    if (
                        self.game.player.hasTarget() &&
                        self.game.player.target.id === entity.id &&
                        self.input.overlay.updateCallback
                    ) {
                        self.input.overlay.updateCallback(
                            entity.id,
                            data.hitPoints
                        );
                    }
                }

                if (data.mana) entity.setMana(data.mana);
            });

            self.messages.onNetwork(function() {
                self.socket.send(Packets.Network, [Packets.NetworkOpcode.Pong]);
            });

            self.messages.onChat(function(info) {
                if (self.game.isDebug()) log.info(info);

                if (info.withBubble) {
                    var entity = self.entities.get(info.id);

                    if (entity) {
                        info.name =
                            info.name.charAt(0).toUpperCase() +
                            info.name.substr(1);

                        self.bubble.create(info.id, info.text, info.duration);
                        self.bubble.setTo(entity);

                        self.audio.play(Modules.AudioTypes.SFX, 'npctalk');
                    }
                }

                if (info.isGlobal) info.name = '[Global] ' + info.name;

                self.input.chatHandler.add(info.name, info.text, info.colour);
            });

            self.messages.onCommand(function(info) {
                /**
