                            [info.amount],
                            entity.x,
                            entity.y
                        );

                        break;

                    case 'mana':
                        self.info.create(
                            Modules.Hits.Mana,
                            [info.amount],
                            entity.x,
                            entity.y
                        );

                        break;
                }

