                        } else {
                            self.bubble.create(
                                info.id,
                                message,
                                self.time,
                                5000
                            );
                            self.bubble.setTo(entity);
                        }

                        var sound = 'npc';

                        self.audio.play(Modules.AudioTypes.SFX, sound);

