                        break;

                    case Packets.EnchantOpcode.Remove:
                        self.interface.enchant.moveBack(type, index);

                        break;
                }
            });

            self.messages.onGuild(function(opcode, info) {
                switch (opcode) {
                    case Packets.GuildOpcode.Create:
                        break;

                    case Packets.GuildOpcode.Join:
                        break;
                }
            });

