                        self.drawText(
                            entity.type === 'player'
                                ? entity.username
                                : entity.name,
                            entity.x + 8,
                            entity.y - (self.drawLevels ? 20 : 10),
                            true,
                            colour,
                            '#000'
                        );
                    }

                    if (
                        self.drawLevels &&
                        (entity.type === 'mob' || entity.type === 'player')
                    ) {
                        self.drawText(
                            'Level ' + entity.level,
                            entity.x + 8,
                            entity.y - (entity.type === 'player' ? 12 : 10),
                            true,
                            colour,
                            '#000'
                        );
                    }

                    if (entity.type === 'item' && entity.count > 1) {
                        self.drawText(
                            entity.count,
                            entity.x + 8,
                            entity.y - 10,
                            true,
                            colour
                        );
                    }
                } else {
                    if (self.game.time - entity.countdownTime > 1000) {
                        entity.countdownTime = self.game.time;
                        entity.counter--;
                    }

                    if (entity.counter <= 0) entity.hasCounter = false;

                    self.drawText(
                        entity.counter,
                        entity.x + 8,
                        entity.y - 10,
                        true,
                        colour
