            self.drawScaledImage(
                context,
                tileset,
                self.getX(tileId + 1, setWidth) * self.tileSize,
                Math.floor(tileId / setWidth) * self.tileSize,
                self.tileSize,
                self.tileSize,
                self.getX(cellId + 1, gridWidth) * self.tileSize,
                Math.floor(cellId / gridWidth) * self.tileSize
