    var devices = [
        'iPad Simulator',
        'iPhone Simulator',
        'iPod Simulator',
        'iPad',
        'iPhone',
        'iPod'
    ];

    if (navigator.platform) {
        while (devices.length)
            if ((navigator.platform = devices.pop())) return true;
    }

    return false;
};

