let Combat = require('../../js/game/entity/character/combat/combat'),
    Messages = require('../../js/network/messages'),
    Packets = require('../../js/network/packets'),
    Modules = require('../../js/util/modules'),
    Utils = require('../../js/util/utils'),
    _ = require('underscore');

class OgreLord extends Combat {
    constructor(character) {
        super(character);

        let self = this;

        self.character = character;

        self.dialogues = [
            'Get outta my swamp',
            'No, not the onion.',
            'My minions give me strength! You stand no chance!'
        ];

        self.minions = [];

        self.lastSpawn = 0;

        self.loaded = false;

        character.projectile = Modules.Projectiles.Boulder;
        character.projectileName = 'projectile-boulder';

        character.onDeath(() => {
            self.reset();
        });
    }

    load() {
        let self = this;

        self.talkingInterval = setInterval(() => {
            if (self.character.hasTarget()) self.forceTalk(self.getMessage());
        }, 9000);

        self.updateInterval = setInterval(() => {
            self.character.armourLevel = 50 + self.minions.length * 15;
        }, 2000);

        self.loaded = true;
    }

    hit(character, target, hitInfo) {
        let self = this;

        if (self.isAttacked()) self.beginMinionAttack();

        if (!character.isNonDiagonal(target)) {
            let distance = character.getDistance(target);

            if (distance < 7) {
                hitInfo.isRanged = true;
                character.attackRange = 7;
            }
        }

        if (self.canSpawn()) self.spawnMinions();

        super.hit(character, target, hitInfo);
    }

    forceTalk(message) {
        let self = this;

        if (!self.world) return;

        self.world.push(Packets.PushOpcode.Regions, {
            regionId: self.character.region,
            message: new Messages.NPC(Packets.NPCOpcode.Talk, {
                id: self.character.instance,
                text: message,
                nonNPC: true
            })
        });
    }

    getMessage() {
        return this.dialogues[Utils.randomInt(0, this.dialogues.length - 1)];
    }

    spawnMinions() {
        let self = this,
            xs = [414, 430, 415, 420, 429],
            ys = [172, 173, 183, 185, 180];

        self.lastSpawn = new Date().getTime();

        self.forceTalk('Now you shall see my true power!');

        for (let i = 0; i < xs.length; i++)
            self.minions.push(self.world.spawnMob(12, xs[i], ys[i]));

        _.each(self.minions, minion => {
            minion.onDeath(() => {
                if (self.isLast()) self.lastSpawn = new Date().getTime();

                self.minions.splice(self.minions.indexOf(minion), 1);
            });

            if (self.isAttacked()) self.beginMinionAttack();
        });

        if (!self.loaded) self.load();
    }

    beginMinionAttack() {
        let self = this;

        if (!self.hasMinions()) return;

        _.each(self.minions, minion => {
            let randomTarget = self.getRandomTarget();

            if (!minion.hasTarget() && randomTarget)
                minion.combat.begin(randomTarget);
        });
    }

    reset() {
        let self = this;

        self.lastSpawn = 0;

        let listCopy = self.minions.slice();

        for (let i = 0; i < listCopy.length; i++) self.world.kill(listCopy[i]);

        clearInterval(self.talkingInterval);
        clearInterval(self.updateInterval);

        self.talkingInterval = null;
        self.updateInterval = null;

        self.loaded = false;
    }

    getRandomTarget() {
        let self = this;

        if (self.isAttacked()) {
            let keys = Object.keys(self.attackers),
                randomAttacker =
                    self.attackers[keys[Utils.randomInt(0, keys.length)]];

            if (randomAttacker) return randomAttacker;
        }

        if (self.character.hasTarget()) return self.character.target;

        return null;
    }

    hasMinions() {
        return this.minions.length > 0;
    }

    isLast() {
        return this.minions.length === 1;
    }

    canSpawn() {
        return (
            new Date().getTime() - this.lastSpawn > 50000 &&
            !this.hasMinions() &&
            this.isAttacked()
        );
    }
}

module.exports = OgreLord;

let Combat = require('../../js/game/entity/character/combat/combat'),
    Utils = require('../../js/util/utils'),
    Messages = require('../../js/network/messages'),
    Packets = require('../../js/network/packets'),
    Modules = require('../../js/util/modules');

class PirateCaptain extends Combat {
    constructor(character) {
        character.spawnDistance = 20;
        super(character);

        let self = this;

        self.character = character;

        self.teleportLocations = [];

        self.lastTeleportIndex = 0;
        self.lastTeleport = 0;

        self.location = {
            x: self.character.x,
            y: self.character.y
        };

        self.load();
    }

    load() {
        let self = this,
            south = { x: 251, y: 574 },
            west = { x: 243, y: 569 },
            east = { x: 258, y: 568 },
            north = { x: 251, y: 563 };

        self.teleportLocations.push(north, south, west, east);
    }

    hit(character, target, hitInfo) {
        let self = this;
        if (self.canTeleport()) self.teleport();
        else super.hit(character, target, hitInfo);
    }

    teleport() {
        let self = this,
            position = self.getRandomPosition();

        if (!position) return;

        self.stop();

        self.lastTeleport = new Date().getTime();
        self.lastTeleportIndex = position.index;

        self.character.setPosition(position.x, position.y);

        if (self.world) {
            self.world.push(Packets.PushOpcode.Regions, {
                regionId: self.character.region,
                message: new Messages.Teleport({
                    id: self.character.instance,
                    x: self.character.x,
                    y: self.character.y,
                    withAnimation: true
                })
            });
        }

        self.forEachAttacker(attacker => {
            attacker.removeTarget();
        });

        if (self.character.hasTarget()) self.begin(self.character.target);
    }

    getRandomPosition() {
        let self = this,
            random = Utils.randomInt(0, self.teleportLocations.length - 1),
            position = self.teleportLocations[random];

        if (!position || random === self.lastTeleportIndex) return null;

        return {
            x: position.x,
            y: position.y,
            index: random
        };
    }

    canTeleport() {
        // Just randomize the teleportation for shits and giggles.
        return (
            new Date().getTime() - this.lastTeleport > 10000 &&
            Utils.randomInt(0, 4) === 2
        );
    }

    getHealthPercentage() {
        // Floor it to avoid random floats
        return Math.floor(
            (this.character.hitPoints / self.character.maxHitPoints) * 100
        );
    }
}

module.exports = PirateCaptain;

let Combat = require('../../js/game/entity/character/combat/combat'),
    Packets = require('../../js/network/packets'),
    Messages = require('../../js/network/messages'),
    Utils = require('../../js/util/utils'),
    _ = require('underscore');

class QueenAnt extends Combat {
    /*
     * The queen ant is a little more complex as it uses
     * AoE attacks and has a stun timer.
     */

    constructor(character) {
        character.spawnDistance = 18;
        super(character);

        let self = this;

        self.character = character;

        self.lastActionThreshold = 10000; // AoE Attack Threshold.

        self.aoeTimeout = null;

        self.aoeCountdown = 5;
        self.aoeRadius = 2;
        self.lastAoE = 0;

        self.minionCount = 7;
        self.lastSpawn = 0;
        self.minions = [];

        self.frozen = false;

        self.character.onDeath(() => {
            /**
             * This is to prevent the boss from dealing
             * any powerful AoE attack after dying.
             */

            self.lastSpawn = 0;

            if (self.aoeTimeout) {
                clearTimeout(self.aoeTimeout);
                self.aoeTimeout = null;
            }

            let listCopy = self.minions.slice();

            for (let i = 0; i < listCopy.length; i++)
                self.world.kill(listCopy[i]);
        });

        self.character.onReturn(() => {
            clearTimeout(self.aoeTimeout);
            self.aoeTimeout = null;
        });
    }

    begin(attacker) {
        let self = this;

        self.resetAoE();

        super.begin(attacker);
    }

    hit(attacker, target, hitInfo) {
        let self = this;

        if (self.frozen) return;

        if (self.canCastAoE()) {
            self.doAoE();
            return;
        }

        if (self.canSpawn()) self.spawnMinions();

        if (self.isAttacked()) self.beginMinionAttack();

        super.hit(attacker, target, hitInfo);
    }

    doAoE() {
        let self = this;

        /**
         * The reason this function does not use its superclass
         * representation is because of the setTimeout function
         * which does not allow us to call super().
         */

        self.resetAoE();

        self.lastHit = self.getTime();

        self.pushFreeze(true);

        self.pushCountdown(self.aoeCountdown);

        self.aoeTimeout = setTimeout(() => {
            self.dealAoE(self.aoeRadius, true);

            self.pushFreeze(false);
        }, 5000);
    }

    spawnMinions() {
        let self = this;

        self.lastSpawn = new Date().getTime();

        for (let i = 0; i < self.minionCount; i++) {
            self.minions.push(
                self.world.spawnMob(13, self.character.x, self.character.y)
            );
        }

        _.each(self.minions, minion => {
            minion.aggressive = true;
            minion.spawnDistance = 12;

            minion.onDeath(() => {
                if (self.isLast()) self.lastSpawn = new Date().getTime();

                self.minions.splice(self.minions.indexOf(minion), 1);
            });

            if (self.isAttacked()) self.beginMinionAttack();
        });
    }

    beginMinionAttack() {
        let self = this;

        if (!self.hasMinions()) return;

        _.each(self.minions, minion => {
            let randomTarget = self.getRandomTarget();

            if (!minion.hasTarget() && randomTarget)
                minion.combat.begin(randomTarget);
        });
    }

    resetAoE() {
        this.lastAoE = new Date().getTime();
    }

    getRandomTarget() {
        let self = this;

        if (self.isAttacked()) {
            let keys = Object.keys(self.attackers),
                randomAttacker =
                    self.attackers[keys[Utils.randomInt(0, keys.length)]];

            if (randomAttacker) return randomAttacker;
        }

        if (self.character.hasTarget()) return self.character.target;

        return null;
    }

    pushFreeze(state) {
        let self = this;

        self.character.frozen = state;
        self.character.stunned = state;
    }

    pushCountdown(count) {
        let self = this;

        self.world.push(Packets.PushOpcode.Regions, {
            regionId: self.character.region,
            message: new Messages.NPC(Packets.NPCOpcode.Countdown, {
                id: self.character.instance,
                countdown: count
            })
        });
    }

    getMinions() {
        let self = this,
            grids = self.world.getGrids();
    }

    isLast() {
        return this.minions.length === 1;
    }

    hasMinions() {
        return this.minions.length > 0;
    }

    canCastAoE() {
        return new Date().getTime() - this.lastAoE > 30000;
    }

    canSpawn() {
        return (
            new Date().getTime() - this.lastSpawn > 45000 &&
            !this.hasMinions() &&
            this.isAttacked()
        );
    }
}

