/* global module */

let Modules = require('../../../../util/modules');

class Trade {
    constructor(player) {
        let self = this;

        self.player = player;
        self.oPlayer = null;

        self.requestee = null;

        self.state = null;
        self.subState = null;

        self.playerItems = [];
        self.oPlayerItems = [];
    }

    start() {
        let self = this;

        self.oPlayer = self.requestee;
        self.state = Modules.Trade.Started;
    }

    stop() {
        let self = this;

        self.oPlayer = null;
        self.state = null;
        self.subState = null;
        self.requestee = null;

        self.playerItems = [];
        self.oPlayerItems = [];
    }

    finalize() {
        let self = this;

        if (!self.player.inventory.containsSpaces(self.oPlayerItems.length))
            return;

        for (let i in self.oPlayerItems) {
            let item = self.oPlayerItems[i];

            if (!item || item.id === -1) continue;

            self.oPlayer.inventory.remove(item.id, item.count, item.index);
            self.player.inventory.add(item);
        }
    }

    select(slot) {
        let self = this,
            item = self.player.inventory.slots[slot];

        if (!item || item.id === -1 || self.playerItems.indexOf(item) < 0)
            return;

        self.playerItems.push(item);
    }

    request(oPlayer) {
        let self = this;

        self.requestee = oPlayer;

        if (oPlayer.trade.getRequestee() === self.player.instance) self.start();
    }

    accept() {
        let self = this;

        self.subState = Modules.Trade.Accepted;

        if (self.oPlayer.trade.subState === Modules.Trade.Accepted) {
            self.finalize();
            self.oPlayer.trade.finalize();
        }
    }

    getRequestee() {
        let self = this;

        if (!self.requestee) return null;

        return self.requestee.instance;
    }

    decline() {
        this.stop();
    }

    isStarted() {
        return this.state !== null;
    }
}

module.exports = Trade;

/* global module */

let Modules = require('../../../../util/modules'),
    Utils = require('../../../../util/utils');

class Warp {
    constructor(player) {
        let self = this;

        self.player = player;

        self.lastWarp = 0;
        self.warpTimeout = 30000;
    }

    warp(id) {
        let self = this;

        if (!self.isCooldown()) {
            self.player.notify(
                'You must wait another ' + self.getDuration() + ' to warp.'
            );
            return;
        }

        let data = Modules.Warps[id];

        if (!data) return;

        let name = data[0],
            x = data[3] ? data[1] + Utils.randomInt(0, 1) : data[1],
            y = data[3] ? data[2] + Utils.randomInt(0, 1) : data[2],
            levelRequirement = data[4];

        log.info('Player Rights: ' + self.player.rights);

        if (self.hasRequirement()) {
            self.player.notify(
                'You must be at least level ' +
                    levelRequirement +
                    ' to warp here!'
            );
            return;
        }

        self.player.teleport(x, y, false, true);

        self.player.notify('You have been warped to ' + name);

        self.lastWarp = new Date().getTime();
    }

    setLastWarp(lastWarp) {
        let self = this;

        if (isNaN(lastWarp)) {
            self.lastWarp = 0;
            self.player.save();
        } else self.lastWarp = lastWarp;
    }

    isCooldown() {
        return (
            this.getDifference() > this.warpTimeout || this.player.rights > 1
        );
    }

    hasRequirement(levelRequirement) {
        return this.player.level < levelRequirement || !this.player.rights > 1;
    }

    getDuration() {
        let self = this,
            difference = this.warpTimeout - self.getDifference();

        if (!difference) return '5 minutes';

        return difference > 60000
            ? Math.ceil(difference / 60000) + ' minutes'
            : Math.floor(difference / 1000) + ' seconds';
    }

    getDifference() {
        return new Date().getTime() - this.lastWarp;
    }
}

module.exports = Warp;

