/* global log, _, Detect, Modules */

define(function() {

    return Class.extend({

        init: function(game) {
            var self = this;

            self.game = game;

            self.audibles = {};
            self.format = 'mp3';

            self.song = null;
            self.songName = null;

            self.enabled = true;

            self.load();
        },

        load: function() {
            var self = this;

            self.music = {
                'codingroom': false,
                'smalltown': false,
                'village': false,
                'beach': false,
                'spookyship': false,
                'meadowofthepast': false
            };

            self.sounds = {
                'loot': false,
                'hit1': false,
                'hit2': false,
                'hurt': false,
                'heal': false,
                'chat': false,
                'revive': false,
                'death': false,
                'firefox': false,
                'achievement': false,
                'kill1': false,
                'kill2': false,
                'noloot': false,
                'teleport': false,
                'chest': false,
                'npc': false,
                'npc-end': false
            };

        },

        parse: function(path, name, channels, callback) {
            var self = this,
                fullPath = path + name + '.' + self.format,
                sound = document.createElement('audio');

            sound.addEventListener('canplaythrough', function(e) {
                this.removeEventListener('canplaythrough', arguments.callee, false);

                if (callback)
                    callback();

            }, false);

            sound.addEventListener('error', function() {
                log.error('The audible: ' + name + ' could not be loaded - unsupported extension?');

                self.audibles[name] = null;
            }, false);

            sound.preload = 'auto';
            sound.autobuffer = true;
            sound.src = fullPath;

            sound.load();

            self.audibles[name] = [sound];

            _.times(channels - 1, function() {
                self.audibles[name].push(sound.cloneNode(true));
            });

            if (name in self.music)
                self.music[name] = true;
            else if (name in self.sounds)
                self.sounds[name] = true;
        },

        play: function(type, name) {
            var self = this;

            if (!self.isEnabled() || !self.fileExists(name))
                return;

            switch(type) {
                case Modules.AudioTypes.Music:

                    self.fadeOut(self.song, function() {
                        self.reset(self.song);
                    });

                    var song = self.get(name);

                    if (!song)
                        return;

                    song.volume = 0;

                    song.play();

                    self.fadeIn(song);

                    self.song = song;

                    break;

                case Modules.AudioTypes.SFX:

                    if (!self.sounds[name])
                        self.parse('audio/sounds/', name, 4);

                    var sound = self.get(name);

                    if (!sound)
                        return;

                    sound.volume = self.getSFXVolume();

                    sound.play();

                    break;
            }
        },

        update: function() {
            var self = this;

            if (!self.isEnabled())
                return;

            var song = self.getMusic(self.songName);

            if (song && !(self.song && self.song.name === song.name)) {
                if (self.game.renderer.mobile)
                    self.reset(self.song);
                else
                    self.fadeSongOut();

                if (song.name in self.music && !self.music[song.name]) {
                    self.parse('audio/music/', song.name, 1);

                    var music = self.audibles[song.name][0];

                    music.loop = true;
                    music.addEventListener('ended', function() {
                        music.play();
                    }, false);
                }

                self.play(Modules.AudioTypes.Music, song.name);

            } else {

                if (self.game.renderer.mobile)
                    self.reset(self.song);
                else
                    self.fadeSongOut();
            }

        },

        fadeIn: function(song) {
            var self = this;

            if (!song || song.fadingIn)
                return;

            self.clearFadeOut(song);

            song.fadingIn = setInterval(function() {
                song.volume += 0.02;

                if (song.volume >= self.getMusicVolume() - 0.02) {
                    song.volume = self.getMusicVolume();
                    self.clearFadeIn(song);
                }

            }, 100);
        },

        fadeOut: function(song, callback) {
            var self = this;

            if (!song || song.fadingOut)
                return;

            self.clearFadeIn(song);

            song.fadingOut = setInterval(function() {

                song.volume -= 0.08;

                if (song.volume <= 0.08) {
                    song.volume = 0;

                    if (callback)
                        callback(song);

                    clearInterval(song.fadingOut);
                }

            }, 100);
        },

        fadeSongOut: function() {
            var self = this;

            if (!self.song)
                return;

            self.fadeOut(self.song, function(song) { self.reset(song); });

            self.song = null;
        },

        clearFadeIn: function(song) {
            if (song.fadingIn) {
                clearInterval(song.fadingIn);
                song.fadingIn = null;
            }
        },

        clearFadeOut: function(song) {
            if (song.fadingOut) {
                clearInterval(song.fadingOut);
                song.fadingOut = null;
            }
        },

        reset: function(song) {
            if (!song || !song.readyState > 0)
                return;

            song.pause();
            song.currentTime = 0;
        },

        stop: function() {
            var self = this;

            if (!self.song)
                return;

            self.fadeOut(self.song, function() {
                self.reset(self.song);
                self.song = null;
            });
        },

        fileExists: function(name) {
            return (name in this.music) || (name in this.sounds);
        },

        get: function(name) {
            var self = this;

            if (!self.audibles[name])
                return null;

            var audible = _.detect(self.audibles[name], function(audible) {
                return audible.ended || audible.paused;
            });

            if (audible && audible.ended)
                audible.currentTime = 0;
            else
                audible = self.audibles[name][0];

            return audible;
        },

        getMusic: function(name) {
            return {
                sound: this.get(name),
                name: name
            }
        },

        setSongVolume: function(volume) {
            this.song.volume = volume;
        },

        getSFXVolume: function() {
            return this.game.storage.data.settings.sfx / 100;
        },

        getMusicVolume: function() {
            return this.game.storage.data.settings.music / 100;
        },

        isEnabled: function() {
            return this.game.storage.data.settings.soundEnabled && this.enabled;
        }

    });

});

/* global _ */

define(['jquery', '../renderer/bubbles/blob'], function($, Blob) {

    return Class.extend({

        init: function(game) {
            var self = this;

            self.game = game;
            self.bubbles = {};

            self.container = $('#bubbles');
        },

        create: function(id, message, duration) {
            var self = this;

            if (self.bubbles[id]) {
                self.bubbles[id].reset(self.game.time);
                $('#' + id + ' p').html(message);
            } else {
                var element = $('<div id=\''+id+'\' class=\'bubble\'><p>'+message+'</p><div class=\'bubbleTip\'></div></div>');

                $(element).appendTo(self.container);

                self.bubbles[id] = new Blob(id, element, duration);

                return self.bubbles[id];
            }
        },

        setTo: function(entity) {
            var self = this;

            var bubble = self.get(entity.id);

            if (!bubble || !entity)
                return;

            var scale = self.game.renderer.getScale(),
                tileSize = 16 * scale,
                x = (entity.x - self.game.getCamera().x) * scale,
                width = parseInt(bubble.element.css('width')) + 24,
                offset = (width / 2) - (tileSize / 2),
                offsetY = -20, y;

            y = ((entity.y - self.game.getCamera().y) * scale) - (tileSize * 2) - offsetY;

            bubble.element.css('left', x - offset + (2 + self.game.renderer.scale) + 'px');
            bubble.element.css('top', y + 'px');
        },

        update: function(time) {
            var self = this;

            _.each(self.bubbles, function(bubble) {
                var entity = self.game.entities.get(bubble.id);

                if (entity)
                    self.setTo(entity);

                if (bubble.isOver(time)) {
                    bubble.destroy();
                    delete self.bubbles[bubble.id];
                }
            });
        },

        get: function(id) {
            var self = this;

            if (id in self.bubbles)
                return self.bubbles[id];

            return null;
        },

        clean: function() {
            var self = this;

            _.each(self.bubbles, function(bubble) {
                bubble.destroy();
            });

            self.bubbles = {};
        },

        destroy: function(id) {
            var self = this,
                bubble = self.get(id);

            if (!bubble)
                return;

            bubble.destroy();
            delete self.bubbles[id];
        }

    });

});

/* global Packets, Modules, log */

define(['jquery'], function($) {

    return Class.extend({

        init: function(game) {
            var self = this;

            self.game = game;

            self.chat = $('#chat');
            self.log = $('#chatLog');
            self.input = $('#chatInput');
            self.button = $('#chatButton');

            self.visible = false;

            self.fadingDuration = 5000;
            self.fadingTimeout = null;

            self.button.click(function() {

                self.button.blur();

                if (self.input.is(':visible'))
                    self.hideInput();
                else
                    self.toggle();

            });
        },

        add: function(source, text, colour) {
            var self = this,
                element = $('<p>' + source + ': ' + text + '</p>');

            self.showChat();

            if (!self.isActive())
                self.hideInput();

            self.hideChat();

            element.css('color', colour ? colour : 'white');

            self.log.append(element);
            self.log.scrollTop(99999);
        },

        key: function(data) {
            var self = this;

            switch(data) {
                case Modules.Keys.Enter:

                    if (self.input.val() === '')
                        self.toggle();
                    else
                        self.send();

                    break;
            }
        },

        send: function() {
            var self = this;

            self.game.socket.send(Packets.Chat, [self.input.val()]);
            self.toggle();
        },

        toggle: function() {
            var self = this;

            self.clean();

            if (self.visible && !self.isActive())
                self.showInput();
            else if (self.visible) {
                self.hideInput();
                self.hideChat();
            } else {
                self.showChat();
                self.showInput();
            }

        },

        showChat: function() {
            var self = this;

            self.chat.fadeIn('fast');

            self.visible = true;
        },

        showInput: function() {
            var self = this;

            self.button.addClass('active');

            self.input.fadeIn('fast');
            self.input.val('');
            self.input.focus();

            self.clean();
        },

        hideChat: function() {
            var self = this;

            if (self.fadingTimeout) {
                clearTimeout(self.fadingTimeout);
                self.fadingTimeout = null;
            }

            self.fadingTimeout = setTimeout(function() {

                if (!self.isActive()) {
                    self.chat.fadeOut('slow');

                    self.visible = false;
                }

            }, self.fadingDuration);
        },

        hideInput: function() {
            var self = this;

            self.button.removeClass('active');

            self.input.val('');
            self.input.fadeOut('fast');
            self.input.blur();

            self.hideChat();
        },

        clean: function() {
            var self = this;

            clearTimeout(self.fadingTimeout);
            self.fadingTimeout = null;
        },

        isActive: function() {
            return this.input.is(':focus');
        }
    });

});

/* global log, _, Modules, Packets */

define(['../renderer/grids', '../entity/objects/chest',
        '../entity/character/character', '../entity/character/player/player',
        '../entity/objects/item', './sprites', '../entity/character/mob/mob',
        '../entity/character/npc/npc', '../entity/objects/projectile'],
    function(Grids, Chest, Character, Player, Item, Sprites, Mob, NPC, Projectile) {

        return Class.extend({

            init: function(game) {
                var self = this;

                self.game = game;
                self.renderer = game.renderer;

                self.grids = null;
                self.sprites = null;

                self.entities = {};
                self.decrepit = {};
            },

            load: function() {
                var self = this;

                self.game.app.sendStatus('Loading sprites');

                if (!self.sprites) {
                    self.sprites = new Sprites(self.game.renderer);

                    self.sprites.onLoadedSprites(function() {
                        self.game.input.loadCursors();
                    });
                }

                self.game.app.sendStatus('Loading grids');

                if (!self.grids)
                    self.grids = new Grids(self.game.map);
            },

