define(['jquery', './pages/state', './pages/ability', './pages/settings', './pages/quest'], function($, State, Ability, Settings, Quest) {

    return Class.extend({

        init: function(game) {
            var self = this;

            self.game = game;

            self.body = $('#profileDialog');
            self.button = $('#profileButton');

            self.next = $('#next');
            self.previous = $('#previous');

            self.activePage = null;
            self.activeIndex = 0;
            self.pages = [];

            self.load();
        },

        load: function() {
            var self = this;

            self.button.click(function() {

                self.game.interface.hideAll();
                self.settings.hide();

                if (self.isVisible()) {
                    self.hide();
                    self.button.removeClass('active');

                } else {
                    self.show();
                    self.button.addClass('active');
                }

                if (!self.activePage.loaded)
                    self.activePage.load();

                self.game.socket.send(Packets.Click, ['profile', self.button.hasClass('active')]);

            });

            self.next.click(function() {
                if (self.activeIndex + 1 < self.pages.length)
                    self.setPage(self.activeIndex + 1);
                else
                    self.next.removeClass('enabled');
            });

            self.previous.click(function() {
                if (self.activeIndex > 0)
                    self.setPage(self.activeIndex - 1);
                else
                    self.previous.removeClass('enabled');
            });

            self.state = new State(self.game);
            self.ability = new Ability(self.game);
            self.settings = new Settings(self.game);
            self.quests = new Quest(self.game);

            self.pages.push(self.state, self.quests, self.ability);

            self.activePage = self.state;

            if (self.activeIndex === 0 && self.activeIndex !== self.pages.length)
                self.next.addClass('enabled');
        },

        update: function() {
            var self = this;

            _.each(self.pages, function(page) { page.update(); });
        },

        resize: function() {
            var self = this;

            _.each(self.pages, function(page) { page.resize(); });
        },

        setPage: function(index) {
            var self = this,
                page = self.pages[index];

            self.clear();

            if (page.isVisible())
                return;

            self.activePage = page;
            self.activeIndex = index;

            if (self.activeIndex + 1 === self.pages.length)
                self.next.removeClass('enabled');
            else if (self.activeIndex === 0)
                self.previous.removeClass('enabled');
            else {
                self.previous.addClass('enabled');
                self.next.addClass('enabled');
            }

            page.show();
        },

        show: function() {
            var self = this;

            self.body.fadeIn('slow');
            self.button.addClass('active');
        },

        hide: function() {
            var self = this;

            self.body.fadeOut('fast');
            self.button.removeClass('active');

            if (self.settings)
                self.settings.hide();
        },

        isVisible: function() {
            return this.body.css('display') === 'block';
        },

        clear: function() {
            var self = this;

            if (self.activePage)
                self.activePage.hide();
        }

    });

});

define(['jquery', './container/container'], function($, Container) {

    return Class.extend({

        init: function(game, intrface) {
            var self = this;

            self.game = game;

            self.body = $('#shop');
            self.shop = $('#shopContainer');
            self.inventory = $('#shopInventorySlots');

            self.player = game.player;
            self.interface = intrface;

            self.container = null;
            self.data = null;

            self.openShop = -1;

            self.items = [];
            self.counts = [];

            self.close = $('#closeShop');

            self.close.css('left', '97%');
            self.close.click(function() {
                self.hide();
            });

        },

        buy: function(event) {
            var self = this,
                id = event.currentTarget.id.substring(11);

            self.game.socket.send(Packets.Shop, [Packets.ShopOpcode.Buy, self.openShop, id, 1]);
        },

        sell: function() {
            var self = this;


        },

        /**
         * The shop file is already built to support full de-initialization of objects when
         * we receive an update about the stocks. So we just use that whenever we want to resize.
         * This is just a temporary fix, in reality, we do not want anyone to actually see the shop
         * do a full refresh when they buy an item or someone else buys an item.
         */

        resize: function() {
            var self = this;

            self.getInventoryList().empty();
            self.getShopList().empty();

            self.update(self.data);
        },

        update: function(data) {
            var self = this;

            self.reset();

            self.container = new Container(data.strings.length);

            //Update the global data to current revision
            self.data = data;

            self.load();
        },

        load: function() {
            var self = this;

            for (var i = 0; i < self.container.size; i++) {
                var shopItem = $('<div id="shopItem' + i + '" class="shopItem"></div>'),
                    string = self.data.strings[i],
                    name = self.data.names[i],
                    count = self.data.counts[i],
                    itemImage, itemCount, itemName, itemBuy;

                if (!string || !name || !count)
                    continue;

                itemImage = $('<div id="shopItemImage' + i + '" class="shopItemImage"></div>');
                itemCount = $('<div id="shopItemCount' + i + '" class="shopItemCount"></div>');
                itemName = $('<div id="shopItemName' + i + '" class="shopItemName"></div>');
                itemBuy = $('<div id="shopItemBuy' + i + '" class="shopItemBuy"></div>');

                itemImage.css('background-image', self.container.getImageFormat(1, string));
                itemCount.html(count);
                itemName.html(name);
                itemBuy.html('Purchase');

                self.container.setSlot(i, {
                    string: string,
                    count: count
                });

                // Bind the itemBuy to the local buy function.
                itemBuy.click(function(event) {
                    self.buy(event);
                });

                var listItem = $('<li></li>');

                shopItem.append(itemImage, itemCount, itemName, itemBuy);

                listItem.append(shopItem);

                self.getShopList().append(listItem);
            }

            var inventoryItems = self.interface.bank.getInventoryList(),
                inventorySize = self.interface.inventory.getSize();

            for (var j = 0; j < inventorySize; j++) {
                var item = $(inventoryItems[j]).clone(),
                    slot = item.find('#bankInventorySlot' + j);

                self.getInventoryList().append(slot);
            }
        },

        reset: function() {
            var self = this;

            self.items = [];
            self.counts = [];

            self.container = null;

            self.getShopList().empty();
            self.getInventoryList().empty();
        },

        open: function(id) {
            var self = this;

            if (!id)
                return;

            self.openShop = id;

            self.body.fadeIn('slow');
        },

        hide: function() {
            var self = this;

            self.openShop = -1;

            self.body.fadeOut('fast');
        },

        getScale: function() {
            return this.game.renderer.getScale();
        },

        isVisible: function() {
            return this.body.css('display') === 'block';
        },

        isShopOpen: function(shopId) {
            return this.isVisible() && this.openShop === shopId;
        },

        getShopList: function() {
            return this.shop.find('ul');
        },

        getInventoryList: function() {
            return this.inventory.find('ul');
        }

    });


});

