            deferredPrompt.userChoice.then(function(choiceResult) {

                if (choiceResult.outcome === 'accepted')
                    log.info('Your PWA has been installed');
                else
                    log.info('User chose to not install your PWA');

                deferredPrompt = null;
            });
        }
    };
    var load = function() {
        $(document).ready(function() {
            app = new App();
            body = $('body');
            chatInput = $('#chatInput');

            addClasses();
