            var self = this;

            if (entity && self.entityGrid[y][x])
                self.entityGrid[y][x][entity.id] = entity;
        },

        addToItemGrid: function(item, x, y) {
            var self = this;

            if (item && self.itemGrid[y][x])
                self.itemGrid[y][x][item.id] = item;
        },

