            _.each(collections, (col) => {
                let collection = database.collection(col);

                collection.deleteOne({
                    username: player.username
                }, (error, result) => {
                    if (error) throw error;

                    if (result)
                        log.info('Player ' + player.username + ' has been deleted.')
