let Messages = require('../../../../network/messages'),
    Packets = require('../../../../network/packets');

class Guild {
    
    constructor(player, data) {
        let self = this;
        
        self.player = player;
        self.data = data;
    }

    join() {
        let self = this;

        if (self.data && self.data.name)
            self.leave();


    }

