/* global module */

let _ = require('underscore');

class Grids {

    constructor(map) {
        let self = this;

        self.map = map;

        self.entityGrid = [];

        self.load();
    }

    load() {
        let self = this;

        for (let i = 0; i < self.map.height; i++) {
            self.entityGrid[i] = [];

            for (let j = 0; j < self.map.width; j++)
                self.entityGrid[i][j] = {};
        }
    }

    updateEntityPosition(entity) {
        let self = this;

        if (entity && entity.oldX === entity.x && entity.oldY === entity.y)
            return;

        self.removeFromEntityGrid(entity, entity.oldX, entity.oldY);
        self.addToEntityGrid(entity, entity.x, entity.y);

        entity.updatePosition();
    }

    addToEntityGrid(entity, x, y) {
        let self = this;

        if (entity && x > 0 && y > 0 && x < self.map.width && x < self.map.height && self.entityGrid[y][x])
            self.entityGrid[y][x][entity.instance] = entity;
    }

    removeFromEntityGrid(entity, x, y) {
        let self = this;

        if (entity && x > 0 && y > 0 && x < self.map.width && y < self.map.height && self.entityGrid[y][x] && entity.instance in self.entityGrid[y][x])
            delete self.entityGrid[y][x][entity.instance];
    }

    getSurroundingEntities(entity, radius, include) {
        let self = this,
            entities = [];

        if (!self.checkBounds(entity.x, entity.y, radius))
            return;

        for (let i = -radius; i < radius + 1; i++) {

            for (let j = -radius; j < radius + 1; j++) {
                let pos = self.entityGrid[entity.y + i][entity.x + j];

                if (_.size(pos) > 0) {
                    _.each(pos,  (pEntity) => {

                        if (!include && pEntity.instance !== entity.instance)
                            entities.push(pEntity);
                    });
                }

            }
        }

        return entities;
    }

    checkBounds(x, y, radius) {
        return x + radius < this.map.width && x - radius > 0 && y + radius < this.map.height && y - radius > 0;
    }

}

module.exports = Grids;

/* global module */

let _ = require('underscore'),
    map = require('../../data/map/world_server');

class Regions {

    constructor(map) {
        let self = this;

        self.map = map;

        self.width = self.map.width;
        self.height = self.map.height;

        self.zoneWidth = self.map.zoneWidth;
        self.zoneHeight = self.map.zoneHeight;

        self.regionWidth = self.map.regionWidth;
        self.regionHeight = self.map.regionHeight;

        self.linkedRegions = {};

        self.loadDoors();
    }

    loadDoors() {
        let self = this,
            doors = map.doors;

        _.each(doors, (door) => {
            let regionId = self.regionIdFromPosition(door.x, door.y),
                linkedRegionId = self.regionIdFromPosition(door.tx, door.ty),
                linkedRegionPosition = self.regionIdToPosition(linkedRegionId);

            if (regionId in self.linkedRegions)
                self.linkedRegions[regionId].push(linkedRegionPosition);
            else
                self.linkedRegions[regionId] = [linkedRegionPosition];
        });
    }

    // y y x y y
    // y y x y y
    // y x x x y
    // y y x y x
    // y y x y y

    getAdjacentRegions(id, offset = 1) {
        let self = this,
            position = self.regionIdToPosition(id),
            x = position.x, y = position.y;

        let list = [];

        for (let i = -offset; i <= offset; i++) // y
            for (let j = -1; j <= 1; j++) // x
                if (i > -2 || i < 2)
                    list.push({ x: x + j, y: y + i });

        _.each(self.linkedRegions[id], (regionPosition) => {

            if (!_.any(list, (regionPosition) => {
                return regionPosition.x === x && regionPosition.y === y;
            })) list.push(regionPosition);

        });

        return _.reject(list, (regionPosition) => {
            let gX = regionPosition.x,
                gY = regionPosition.y;

            return gX < 0 || gY < 0 || gX >= self.regionWidth || gY >= self.regionHeight;
        });

    }

    forEachRegion(callback) {
        let self = this;

        for (let x = 0; x < self.regionWidth; x++)
            for (let y = 0; y < self.regionHeight; y++)
                callback(x + '-' + y)
    }

    forEachAdjacentRegion(regionId, callback, offset) {
        let self = this;

        if (!regionId)
            return;

        _.each(self.getAdjacentRegions(regionId, offset), (position) => {
            callback(position.x + '-' + position.y);
        });
    }

    regionIdFromPosition(x, y) {
        return (Math.floor(x / this.zoneWidth) + '-' + (Math.floor(y / this.zoneHeight)));
    }

    regionIdToPosition(id) {
        let position = id.split('-');

        return {
            x: parseInt(position[0], 10),
            y: parseInt(position[1], 10)
        }
    }

    regionIdToCoordinates(id) {
        let self = this,
            position = id.split('-');

        return {
            x: parseInt(position[0]) * self.zoneWidth,
            y: parseInt(position[1]) * self.zoneHeight
        }
    }
}

module.exports = Regions;

